
import type { Chapter } from '../data';

export const chemicalBondingQuestions: Chapter = {
    id: 203,
    name: 'Chemical Bonding',
    questions: [
        // Easy: 70 questions
        { id: 203001, text: 'Which type of bond is formed by the complete transfer of electrons from one atom to another?', options: ['Covalent bond', 'Ionic bond', 'Hydrogen bond', 'Metallic bond'], answer: 'Ionic bond', difficulty: 'Easy', pageReference: 65, concepts: ['ionic bond'], isPastPaper: false, explanation: 'Ionic bonds are formed by the electrostatic attraction between oppositely charged ions, which are formed by the complete transfer of one or more electrons from one atom to another.' },
        { id: 203002, text: 'The shape of the methane (CH₄) molecule is:', options: ['Linear', 'Trigonal planar', 'Tetrahedral', 'Octahedral'], answer: 'Tetrahedral', difficulty: 'Easy', pageReference: 72, concepts: ['vsepr theory', 'molecular geometry'], isPastPaper: true, explanation: 'In methane (CH₄), the central carbon atom forms four single bonds with four hydrogen atoms. According to VSEPR theory, the four bonding pairs repel each other equally, resulting in a tetrahedral geometry with bond angles of 109.5°.' },
        { id: 203003, text: 'A double bond consists of:', options: ['Two sigma bonds', 'Two pi bonds', 'One sigma and one pi bond', 'One sigma and two pi bonds'], answer: 'One sigma and one pi bond', difficulty: 'Easy', pageReference: 75, concepts: ['covalent bond', 'double bond'], isPastPaper: false, explanation: 'A double covalent bond is formed by the sharing of two electron pairs between two atoms. It consists of one strong sigma (σ) bond and one weaker pi (π) bond.' },
        { id: 203004, text: 'Which of the following molecules has a hydrogen bond?', options: ['CH₄', 'H₂S', 'H₂O', 'HCl'], answer: 'H₂O', difficulty: 'Easy', pageReference: 80, concepts: ['hydrogen bond'], isPastPaper: false, explanation: 'Hydrogen bonding occurs when hydrogen is bonded to a highly electronegative atom like Nitrogen (N), Oxygen (O), or Fluorine (F). In water (H₂O), hydrogen is bonded to oxygen, leading to strong hydrogen bonds between molecules.' },
        { id: 203005, text: 'What is the bond order of the O₂ molecule?', options: ['1', '2', '3', '1.5'], answer: '2', difficulty: 'Easy', pageReference: 82, concepts: ['molecular orbital theory', 'bond order'], isPastPaper: true, explanation: 'According to Molecular Orbital Theory, the bond order is calculated as (Number of bonding electrons - Number of antibonding electrons) / 2. For O₂, this is (10 - 6) / 2 = 2.' },
        { id: 203006, text: 'What is the octet rule?', options: ['Atoms tend to have 8 valence electrons', 'Atoms tend to have 2 valence electrons', 'Atoms tend to lose all electrons', 'Atoms tend to gain 8 electrons'], answer: 'Atoms tend to have 8 valence electrons', difficulty: 'Easy', pageReference: 66, concepts: ['octet rule'], isPastPaper: false, explanation: 'The octet rule states that atoms tend to bond in such a way that they each have eight electrons in their valence shell, giving them the same electronic configuration as a noble gas.'},
        { id: 203007, text: 'A covalent bond is formed by:', options: ['Transfer of electrons', 'Sharing of electrons', 'Donation of electrons', 'Acceptance of electrons'], answer: 'Sharing of electrons', difficulty: 'Easy', pageReference: 68, concepts: ['covalent bond'], isPastPaper: false, explanation: 'A covalent bond is a chemical bond that involves the sharing of electron pairs between atoms.'},
        { id: 203008, text: 'Which element is the most electronegative?', options: ['Oxygen', 'Chlorine', 'Fluorine', 'Nitrogen'], answer: 'Fluorine', difficulty: 'Easy', pageReference: 70, concepts: ['electronegativity'], isPastPaper: false, explanation: 'Fluorine is the most electronegative element, with a value of approximately 3.98 on the Pauling scale.'},
        { id: 203009, text: 'The geometry of a molecule with sp hybridization is:', options: ['Tetrahedral', 'Trigonal planar', 'Linear', 'Bent'], answer: 'Linear', difficulty: 'Easy', pageReference: 77, concepts: ['hybridization', 'molecular geometry'], isPastPaper: false, explanation: 'sp hybridization involves the mixing of one s and one p orbital, resulting in two equivalent sp hybrid orbitals that are oriented 180° apart, leading to a linear geometry.'},
        { id: 203010, text: 'A bond in which both electrons are contributed by one atom is called a:', options: ['Covalent bond', 'Ionic bond', 'Metallic bond', 'Coordinate bond'], answer: 'Coordinate bond', difficulty: 'Easy', pageReference: 69, concepts: ['coordinate bond', 'dative bond'], isPastPaper: false, explanation: 'A coordinate (or dative) bond is a type of covalent bond where both shared electrons are donated by the same atom.'},
        { id: 203011, text: 'What is the bond angle in a water (H₂O) molecule?', options: ['180°', '120°', '109.5°', '104.5°'], answer: '104.5°', difficulty: 'Easy', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: false, explanation: 'Due to the presence of two lone pairs on the oxygen atom, the bond angle in water is compressed from the ideal tetrahedral angle to approximately 104.5°.'},
        { id: 203012, text: 'The tendency of an atom to attract a shared pair of electrons is known as:', options: ['Ionization enthalpy', 'Electron gain enthalpy', 'Electronegativity', 'Covalency'], answer: 'Electronegativity', difficulty: 'Easy', pageReference: 70, concepts: ['electronegativity'], isPastPaper: false, explanation: 'Electronegativity is a measure of the ability of an atom in a chemical compound to attract shared electrons to itself.'},
        { id: 203013, text: 'A triple bond consists of:', options: ['Three sigma bonds', 'Three pi bonds', 'One sigma and two pi bonds', 'Two sigma and one pi bond'], answer: 'One sigma and two pi bonds', difficulty: 'Easy', pageReference: 75, concepts: ['covalent bond', 'triple bond'], isPastPaper: false, explanation: 'A triple bond between two atoms is composed of one sigma (σ) bond and two pi (π) bonds.'},
        { id: 203014, text: 'Which of the following molecules is nonpolar?', options: ['NH₃', 'H₂O', 'CO₂', 'HCl'], answer: 'CO₂', difficulty: 'Easy', pageReference: 74, concepts: ['molecular polarity', 'dipole moment'], isPastPaper: true, explanation: 'Carbon dioxide (CO₂) has a linear shape with two polar C=O bonds. The individual bond dipoles are equal in magnitude and opposite in direction, so they cancel each other out, making the molecule nonpolar.'},
        { id: 203015, text: 'The formation of an ionic bond is favored by:', options: ['Low ionization enthalpy of the metal', 'High electron gain enthalpy of the non-metal', 'High lattice enthalpy', 'All of the above'], answer: 'All of the above', difficulty: 'Easy', pageReference: 67, concepts: ['ionic bond', 'born-haber cycle'], isPastPaper: false, explanation: 'Formation of a stable ionic compound is favored by low ionization enthalpy of the cation-forming element, high (negative) electron gain enthalpy of the anion-forming element, and high lattice enthalpy of the resulting crystal.'},
        { id: 203016, text: 'What type of forces exist between molecules of a noble gas?', options: ['Ionic bonds', 'Covalent bonds', 'Hydrogen bonds', 'Van der Waals forces'], answer: 'Van der Waals forces', difficulty: 'Easy', pageReference: 81, concepts: ['intermolecular forces', 'van der waals forces'], isPastPaper: false, explanation: 'Noble gases exist as individual atoms and the only forces between them are weak, temporary induced dipole-induced dipole interactions, also known as London dispersion forces (a type of Van der Waals force).'},
        { id: 203017, text: 'How many lone pairs are on the central atom in an ammonia (NH₃) molecule?', options: ['0', '1', '2', '3'], answer: '1', difficulty: 'Easy', pageReference: 72, concepts: ['vsepr theory', 'lewis structure'], isPastPaper: false, explanation: 'In ammonia, the central nitrogen atom has 5 valence electrons. It forms 3 single bonds with hydrogen atoms, using 3 electrons. The remaining 2 electrons form one lone pair.'},
        { id: 203018, text: 'Which theory explains the shape of molecules based on the repulsion between electron pairs?', options: ['Valence Bond Theory', 'Molecular Orbital Theory', 'VSEPR Theory', 'Lewis Theory'], answer: 'VSEPR Theory', difficulty: 'Easy', pageReference: 72, concepts: ['vsepr theory'], isPastPaper: false, explanation: 'The Valence Shell Electron Pair Repulsion (VSEPR) theory is a model used to predict the geometry of individual molecules from the number of electron pairs surrounding their central atoms.'},
        { id: 203019, text: 'What is the hybridization of the carbon atoms in ethene (C₂H₄)?', options: ['sp', 'sp²', 'sp³', 'sp³d'], answer: 'sp²', difficulty: 'Easy', pageReference: 78, concepts: ['hybridization'], isPastPaper: false, explanation: 'In ethene, each carbon atom is bonded to two hydrogen atoms and one other carbon atom (via a double bond). This requires three hybrid orbitals, which are formed by sp² hybridization.'},
        { id: 203020, text: 'The bond order of N₂ is:', options: ['1', '2', '2.5', '3'], answer: '3', difficulty: 'Easy', pageReference: 82, concepts: ['molecular orbital theory', 'bond order'], isPastPaper: false, explanation: 'Nitrogen (N₂) has a triple bond between the two nitrogen atoms. In MOT, the bond order is (10-4)/2 = 3.'},
        { id: 203021, text: 'Which of the following is a characteristic of ionic compounds?', options: ['Low melting point', 'Good conductor in solid state', 'Soluble in nonpolar solvents', 'High melting point'], answer: 'High melting point', difficulty: 'Easy', pageReference: 67, concepts: ['ionic compounds', 'properties'], isPastPaper: false, explanation: 'Ionic compounds have strong electrostatic forces between ions in a crystal lattice, requiring a large amount of energy to break, which results in high melting points.'},
        { id: 203022, text: 'What is a sigma (σ) bond?', options: ['A bond formed by sideways overlap of orbitals', 'A bond formed by head-on overlap of orbitals', 'A bond with two regions of electron density', 'A weak bond'], answer: 'A bond formed by head-on overlap of orbitals', difficulty: 'Easy', pageReference: 75, concepts: ['sigma bond', 'covalent bond'], isPastPaper: false, explanation: 'A sigma bond is formed by the direct, head-on overlap of atomic orbitals along the internuclear axis.'},
        { id: 203023, text: 'What is a pi (π) bond?', options: ['A bond formed by sideways overlap of p-orbitals', 'A bond formed by head-on overlap of orbitals', 'The strongest type of covalent bond', 'A bond present in all molecules'], answer: 'A bond formed by sideways overlap of p-orbitals', difficulty: 'Easy', pageReference: 75, concepts: ['pi bond', 'covalent bond'], isPastPaper: false, explanation: 'A pi bond is formed by the lateral or sideways overlap of unhybridized p-orbitals above and below the internuclear axis.'},
        { id: 203024, text: 'The geometry of the BF₃ molecule is:', options: ['Tetrahedral', 'Trigonal pyramidal', 'Trigonal planar', 'Linear'], answer: 'Trigonal planar', difficulty: 'Easy', pageReference: 72, concepts: ['vsepr theory'], isPastPaper: false, explanation: 'In BF₃, the central boron atom has 3 bond pairs and 0 lone pairs. The electron pairs repel to be as far apart as possible, resulting in a trigonal planar geometry with 120° bond angles.'},
        { id: 203025, text: 'Which of the following molecules contains a coordinate bond?', options: ['CH₄', 'NH₃', 'H₃O⁺', 'H₂O'], answer: 'H₃O⁺', difficulty: 'Easy', pageReference: 69, concepts: ['coordinate bond'], isPastPaper: false, explanation: 'The hydronium ion (H₃O⁺) is formed when a water molecule (H₂O) donates one of its lone pairs to a proton (H⁺), forming a coordinate covalent bond.'},
        { id: 203026, text: 'Metallic bonds involve:', options: ['Sharing of electrons between two atoms', 'Transfer of electrons between atoms', 'Electrostatic attraction between positive ions and a sea of delocalized electrons', 'Attraction between polar molecules'], answer: 'Electrostatic attraction between positive ions and a sea of delocalized electrons', difficulty: 'Easy', pageReference: 81, concepts: ['metallic bond'], isPastPaper: false, explanation: 'The metallic bond is the force of attraction between valence electrons and the metal cations. It is described as a sea of delocalized electrons surrounding a lattice of positive ions.'},
        { id: 203027, text: 'Which type of hybridization leads to a trigonal planar geometry?', options: ['sp', 'sp²', 'sp³', 'dsp²'], answer: 'sp²', difficulty: 'Easy', pageReference: 78, concepts: ['hybridization', 'molecular geometry'], isPastPaper: false, explanation: 'sp² hybridization creates three equivalent hybrid orbitals that are 120° apart in a single plane, leading to a trigonal planar arrangement.'},
        { id: 203028, text: 'The ability of a substance to dissolve in a given solvent is called:', options: ['Solubility', 'Miscibility', 'Polarity', 'Enthalpy'], answer: 'Solubility', difficulty: 'Easy', pageReference: 67, concepts: ['solubility'], isPastPaper: false, explanation: 'Solubility is the property of a solid, liquid, or gaseous chemical substance called solute to dissolve in a solid, liquid, or gaseous solvent.'},
        { id: 203029, text: 'In the formation of NaCl, sodium atom ______ an electron.', options: ['gains', 'loses', 'shares', 'donates'], answer: 'loses', difficulty: 'Easy', pageReference: 65, concepts: ['ionic bond'], isPastPaper: false, explanation: 'Sodium (Na) is a metal that readily loses one valence electron to form a stable Na⁺ cation.'},
        { id: 203030, text: 'In the formation of Cl₂, chlorine atoms ______ electrons.', options: ['gain', 'lose', 'share', 'transfer'], answer: 'share', difficulty: 'Easy', pageReference: 68, concepts: ['covalent bond'], isPastPaper: false, explanation: 'Two chlorine atoms share a pair of electrons to form a single covalent bond, allowing both atoms to achieve a stable octet.'},
        { id: 203031, text: 'A bond where electrons are shared unequally is called a:', options: ['Nonpolar covalent bond', 'Polar covalent bond', 'Ionic bond', 'Metallic bond'], answer: 'Polar covalent bond', difficulty: 'Easy', pageReference: 71, concepts: ['polar covalent bond'], isPastPaper: false, explanation: 'A polar covalent bond occurs when there is a significant difference in electronegativity between the two bonded atoms, causing an unequal sharing of electrons.'},
        { id: 203032, text: 'The shape of an acetylene (C₂H₂) molecule is:', options: ['Linear', 'Bent', 'Trigonal planar', 'Tetrahedral'], answer: 'Linear', difficulty: 'Easy', pageReference: 77, concepts: ['molecular geometry', 'hybridization'], isPastPaper: false, explanation: 'In acetylene, each carbon atom is sp hybridized, forming a triple bond with the other carbon and a single bond with a hydrogen. The geometry is linear with 180° bond angles.'},
        { id: 203033, text: 'How many sigma and pi bonds are in a nitrogen molecule (N₂)?', options: ['1 σ, 1 π', '2 σ, 1 π', '1 σ, 2 π', '3 σ, 0 π'], answer: '1 σ, 2 π', difficulty: 'Easy', pageReference: 75, concepts: ['sigma bond', 'pi bond'], isPastPaper: false, explanation: 'The triple bond in a nitrogen molecule (N≡N) consists of one sigma (σ) bond and two pi (π) bonds.'},
        { id: 203034, text: 'What is lattice enthalpy?', options: ['Energy released when one mole of an ionic compound is formed from its gaseous ions', 'Energy required to break one mole of bonds', 'Energy required to form one mole of a compound from its elements', 'Energy change when a substance dissolves'], answer: 'Energy released when one mole of an ionic compound is formed from its gaseous ions', difficulty: 'Easy', pageReference: 67, concepts: ['lattice enthalpy'], isPastPaper: false, explanation: 'Lattice enthalpy is a measure of the strength of the forces between the ions in an ionic solid. It is formally defined as the enthalpy change when one mole of an ionic compound is formed from its gaseous ions.'},
        { id: 203035, text: 'Which of the following is an exception to the octet rule?', options: ['CH₄', 'H₂O', 'PCl₅', 'CO₂'], answer: 'PCl₅', difficulty: 'Easy', pageReference: 66, concepts: ['octet rule', 'expanded octet'], isPastPaper: false, explanation: 'In phosphorus pentachloride (PCl₅), the central phosphorus atom is bonded to five chlorine atoms, having 10 valence electrons, which is an example of an expanded octet.'},
        { id: 203036, text: 'The bond formed between two non-metal atoms is usually:', options: ['Ionic', 'Covalent', 'Metallic', 'Coordinate'], answer: 'Covalent', difficulty: 'Easy', pageReference: 68, concepts: ['covalent bond'], isPastPaper: false, explanation: 'Non-metal atoms have high electronegativities and tend to share electrons to achieve a stable electron configuration, forming covalent bonds.'},
        { id: 203037, text: 'In VSEPR theory, which repels more strongly?', options: ['Bond pair - bond pair', 'Lone pair - bond pair', 'Lone pair - lone pair', 'All repulsions are equal'], answer: 'Lone pair - lone pair', difficulty: 'Easy', pageReference: 72, concepts: ['vsepr theory'], isPastPaper: false, explanation: 'The order of repulsion strength is: Lone Pair-Lone Pair > Lone Pair-Bond Pair > Bond Pair-Bond Pair. Lone pairs occupy more space around the central atom.'},
        { id: 203038, text: 'Dipole moment is a measure of:', options: ['Bond strength', 'Bond length', 'Polarity of a bond or molecule', 'Electronegativity'], answer: 'Polarity of a bond or molecule', difficulty: 'Easy', pageReference: 74, concepts: ['dipole moment'], isPastPaper: false, explanation: 'Dipole moment (μ) is the product of the magnitude of the charge and the distance of separation between the charges, and it quantifies the polarity of a chemical bond or an entire molecule.'},
        { id: 203039, text: 'Which of the following elements can form hydrogen bonds?', options: ['Carbon', 'Sulphur', 'Oxygen', 'Chlorine'], answer: 'Oxygen', difficulty: 'Easy', pageReference: 80, concepts: ['hydrogen bond'], isPastPaper: false, explanation: 'Hydrogen bonding is a strong type of dipole-dipole interaction that occurs when hydrogen is bonded to a highly electronegative atom like N, O, or F.'},
        { id: 203040, text: 'The number of bonding electron pairs in CO₂ is:', options: ['2', '3', '4', '1'], answer: '4', difficulty: 'Easy', pageReference: 68, concepts: ['lewis structure'], isPastPaper: false, explanation: 'The Lewis structure of carbon dioxide is O=C=O. There are two double bonds, which consist of a total of 4 shared electron pairs.'},
        { id: 203041, text: 'What is the formal charge on the central oxygen atom in ozone (O₃)?', options: ['0', '+1', '-1', '+2'], answer: '+1', difficulty: 'Easy', pageReference: 69, concepts: ['formal charge'], isPastPaper: false, explanation: 'In the common resonance structure of ozone, the central oxygen atom has 1 lone pair and forms 3 bonds (one double, one single). Formal Charge = 6 (valence) - 2 (lone pair e⁻) - 1/2 * 6 (bonding e⁻) = +1.'},
        { id: 203042, text: 'Which of these has the shortest bond length?', options: ['C-C', 'C=C', 'C≡C', 'All have the same length'], answer: 'C≡C', difficulty: 'Easy', pageReference: 71, concepts: ['bond length'], isPastPaper: false, explanation: 'Bond length decreases as bond order increases. A triple bond (C≡C) is shorter and stronger than a double bond (C=C), which is shorter and stronger than a single bond (C-C).'},
        { id: 203043, text: 'Which of these has the highest bond enthalpy?', options: ['O-H', 'C-H', 'N-H', 'F-H'], answer: 'F-H', difficulty: 'Easy', pageReference: 71, concepts: ['bond enthalpy'], isPastPaper: false, explanation: 'The F-H bond is the most polar due to the high electronegativity of fluorine, and it is also very short. These factors contribute to it having a very high bond enthalpy.'},
        { id: 203044, text: 'Resonance structures are used when:', options: ['A molecule has a double bond', 'A molecule has a triple bond', 'A single Lewis structure cannot describe the molecule accurately', 'A molecule is nonpolar'], answer: 'A single Lewis structure cannot describe the molecule accurately', difficulty: 'Easy', pageReference: 73, concepts: ['resonance'], isPastPaper: false, explanation: 'Resonance is a concept used to describe delocalized electrons within certain molecules or ions where the bonding cannot be expressed by a single Lewis formula.'},
        { id: 203045, text: 'The shape of the PCl₅ molecule is:', options: ['Tetrahedral', 'Trigonal planar', 'Trigonal bipyramidal', 'Octahedral'], answer: 'Trigonal bipyramidal', difficulty: 'Easy', pageReference: 72, concepts: ['vsepr theory'], isPastPaper: false, explanation: 'PCl₅ has 5 bonding pairs and 0 lone pairs around the central phosphorus atom. This leads to a trigonal bipyramidal geometry.'},
        { id: 203046, text: 'A molecule is said to be polar if it has a net:', options: ['Zero dipole moment', 'Non-zero dipole moment', 'Zero charge', 'Non-zero charge'], answer: 'Non-zero dipole moment', difficulty: 'Easy', pageReference: 74, concepts: ['molecular polarity', 'dipole moment'], isPastPaper: false, explanation: 'Polarity of a molecule is determined by its net dipole moment. If the individual bond dipoles do not cancel out due to molecular geometry, the molecule has a net dipole moment and is polar.'},
        { id: 203047, text: 'How many anti-bonding electrons are in the O₂ molecule?', options: ['2', '4', '6', '8'], answer: '6', difficulty: 'Easy', pageReference: 82, concepts: ['molecular orbital theory'], isPastPaper: false, explanation: 'The molecular orbital configuration of O₂ is (σ1s)²(σ*1s)²(σ2s)²(σ*2s)²(σ2pz)²(π2px)²(π2py)²(π*2px)¹(π*2py)¹. The antibonding orbitals (σ*1s, σ*2s, π*2px, π*2py) contain a total of 2+2+1+1 = 6 electrons.'},
        { id: 203048, text: 'What is bond order?', options: ['The number of bonds between two atoms', 'Half the difference between bonding and anti-bonding electrons', 'The strength of a bond', 'Both A and B are related definitions'], answer: 'Both A and B are related definitions', difficulty: 'Easy', pageReference: 82, concepts: ['bond order'], isPastPaper: false, explanation: 'In simple Lewis structures, bond order is the number of bonds (1 for single, 2 for double, etc.). In Molecular Orbital Theory, it is precisely defined as 1/2 * (bonding e⁻ - antibonding e⁻). A higher bond order indicates a stronger bond.'},
        { id: 203049, text: 'Hydrogen bonding is a special case of which type of interaction?', options: ['Ion-dipole', 'Dipole-dipole', 'London dispersion forces', 'Covalent bonding'], answer: 'Dipole-dipole', difficulty: 'Easy', pageReference: 80, concepts: ['hydrogen bond', 'intermolecular forces'], isPastPaper: false, explanation: 'Hydrogen bonding is an especially strong type of dipole-dipole interaction that occurs when hydrogen is bonded to a very electronegative atom (N, O, or F).'},
        { id: 203050, text: 'The bond angle in BeCl₂ is:', options: ['90°', '109.5°', '120°', '180°'], answer: '180°', difficulty: 'Easy', pageReference: 72, concepts: ['vsepr theory'], isPastPaper: false, explanation: 'BeCl₂ has a central beryllium atom with two bonding pairs and no lone pairs. To maximize the distance between them, the bond angle is 180°, resulting in a linear molecule.'},
        { id: 203051, text: 'Which of the following is not a type of Van der Waals force?', options: ['Dipole-dipole interaction', 'Dipole-induced dipole interaction', 'London dispersion force', 'Hydrogen bond'], answer: 'Hydrogen bond', difficulty: 'Easy', pageReference: 81, concepts: ['van der waals forces', 'hydrogen bond'], isPastPaper: false, explanation: 'While hydrogen bonding is a type of intermolecular force, it is significantly stronger than the other Van der Waals forces and is often classified separately.'},
        { id: 203052, text: 'In Valence Bond Theory, a covalent bond is formed by the:', options: ['Overlap of atomic orbitals', 'Transfer of electrons', 'Formation of molecular orbitals', 'Electrostatic attraction'], answer: 'Overlap of atomic orbitals', difficulty: 'Easy', pageReference: 75, concepts: ['valence bond theory'], isPastPaper: false, explanation: 'Valence Bond Theory describes the formation of a covalent bond as the overlap of half-filled atomic orbitals from two different atoms.'},
        { id: 203053, text: 'The concept of hybridization was introduced to explain:', options: ['The shape of ionic compounds', 'The equivalent nature of bonds in molecules like methane', 'The colors of compounds', 'The conductivity of metals'], answer: 'The equivalent nature of bonds in molecules like methane', difficulty: 'Easy', pageReference: 77, concepts: ['hybridization'], isPastPaper: false, explanation: 'Hybridization was proposed by Linus Pauling to explain why the four C-H bonds in methane are identical in length and strength, despite being formed from one s-orbital and three p-orbitals.'},
        { id: 203054, text: 'According to MOT, a molecule is stable if:', options: ['Bonding electrons > Antibonding electrons', 'Antibonding electrons > Bonding electrons', 'Bonding electrons = Antibonding electrons', 'There are no antibonding electrons'], answer: 'Bonding electrons > Antibonding electrons', difficulty: 'Easy', pageReference: 82, concepts: ['molecular orbital theory'], isPastPaper: false, explanation: 'A positive bond order (where the number of electrons in bonding orbitals is greater than in antibonding orbitals) indicates a stable molecule.'},
        { id: 203055, text: 'Which of the following is diamagnetic?', options: ['O₂', 'O₂⁺', 'O₂⁻', 'N₂'], answer: 'N₂', difficulty: 'Easy', pageReference: 83, concepts: ['molecular orbital theory', 'diamagnetism'], isPastPaper: false, explanation: 'The molecular orbital diagram for N₂ shows that all electrons are paired, making it diamagnetic. O₂, O₂⁺, and O₂⁻ all have unpaired electrons and are paramagnetic.'},
        { id: 203056, text: 'Bond length is the distance between:', options: ['The nuclei of two bonded atoms', 'The outermost shells of two bonded atoms', 'Two lone pairs', 'The centers of two molecules'], answer: 'The nuclei of two bonded atoms', difficulty: 'Easy', pageReference: 71, concepts: ['bond length'], isPastPaper: false, explanation: 'Bond length or bond distance is the average distance between the nuclei of two bonded atoms in a molecule.'},
        { id: 203057, text: 'Bond enthalpy is the energy required to:', options: ['Form one mole of bonds', 'Break one mole of bonds in the gaseous state', 'Melt one mole of a solid', 'Boil one mole of a liquid'], answer: 'Break one mole of bonds in the gaseous state', difficulty: 'Easy', pageReference: 71, concepts: ['bond enthalpy'], isPastPaper: false, explanation: 'Bond enthalpy is the measure of bond strength in a chemical bond. It is the heat required to break one mole of molecules into their individual atoms.'},
        { id: 203058, text: 'What is the geometry of the SF₄ molecule?', options: ['Tetrahedral', 'Square planar', 'See-saw', 'Trigonal planar'], answer: 'See-saw', difficulty: 'Easy', pageReference: 72, concepts: ['vsepr theory'], isPastPaper: true, explanation: 'In SF₄, the central sulfur atom has 4 bonding pairs and 1 lone pair. This AX₄E₁ arrangement results in a see-saw molecular geometry.'},
        { id: 203059, text: 'Fajan\'s rules are used to predict the degree of:', options: ['Ionic character in a covalent bond', 'Covalent character in an ionic bond', 'Metallic character', 'Bond strength'], answer: 'Covalent character in an ionic bond', difficulty: 'Easy', pageReference: 71, concepts: ['fajans rules'], isPastPaper: false, explanation: 'Fajan\'s rules predict whether a chemical bond is expected to be more ionic or more covalent, based on the charge of the cation and the relative sizes of the cation and anion.'},
        { id: 203060, text: 'Which of the following has the highest lattice enthalpy?', options: ['LiF', 'NaCl', 'KBr', 'CsI'], answer: 'LiF', difficulty: 'Easy', pageReference: 67, concepts: ['lattice enthalpy'], isPastPaper: false, explanation: 'Lattice enthalpy is greatest for small, highly charged ions. Li⁺ and F⁻ are the smallest ions among the options, leading to the strongest electrostatic attraction and highest lattice enthalpy.'},
        { id: 203061, text: 'A nonpolar molecule can have polar bonds.', options: ['True', 'False', 'Only for diatomic molecules', 'Only for noble gases'], answer: 'True', difficulty: 'Easy', pageReference: 74, concepts: ['molecular polarity'], isPastPaper: false, explanation: 'True. In molecules with a symmetrical shape, such as CO₂ or CCl₄, the individual bond dipoles can cancel each other out, resulting in a nonpolar molecule even though the bonds themselves are polar.'},
        { id: 203062, text: 'The hybridization of the Xenon atom in XeF₄ is:', options: ['sp³', 'sp³d', 'sp³d²', 'sp²'], answer: 'sp³d²', difficulty: 'Easy', pageReference: 78, concepts: ['hybridization'], isPastPaper: true, explanation: 'In XeF₄, Xenon has 4 bond pairs and 2 lone pairs, for a total of 6 electron domains. This requires six hybrid orbitals, corresponding to sp³d² hybridization.'},
        { id: 203063, text: 'What is the shape of the I₃⁻ ion?', options: ['Linear', 'Trigonal planar', 'Bent', 'Tetrahedral'], answer: 'Linear', difficulty: 'Easy', pageReference: 72, concepts: ['vsepr theory'], isPastPaper: true, explanation: 'In the triiodide ion (I₃⁻), the central iodine atom has 2 bonding pairs and 3 lone pairs. The lone pairs occupy the equatorial positions of a trigonal bipyramid, forcing the bonding pairs into a linear arrangement.'},
        { id: 203064, text: 'The C=O bond is:', options: ['Polar', 'Nonpolar', 'Ionic', 'Metallic'], answer: 'Polar', difficulty: 'Easy', pageReference: 71, concepts: ['bond polarity'], isPastPaper: false, explanation: 'Oxygen is more electronegative than carbon, so the electrons in the C=O bond are shared unequally, creating a polar covalent bond with a partial negative charge on the oxygen and a partial positive charge on the carbon.'},
        { id: 203065, text: 'The number of possible resonance structures for the carbonate ion (CO₃²⁻) is:', options: ['1', '2', '3', '4'], answer: '3', difficulty: 'Easy', pageReference: 73, concepts: ['resonance'], isPastPaper: false, explanation: 'The carbonate ion has three equivalent resonance structures, where the double bond is delocalized across the three oxygen atoms.'},
        { id: 203066, text: 'Which type of bond is weakest?', options: ['Covalent bond', 'Ionic bond', 'Hydrogen bond', 'Metallic bond'], answer: 'Hydrogen bond', difficulty: 'Easy', pageReference: 81, concepts: ['bond strength', 'intermolecular forces'], isPastPaper: false, explanation: 'While hydrogen bonds are the strongest type of intermolecular force, they are significantly weaker than intramolecular bonds like covalent and ionic bonds.'},
        { id: 203067, text: 'A bonding molecular orbital is formed by:', options: ['Destructive interference of atomic orbitals', 'Constructive interference of atomic orbitals', 'Overlap of s and p orbitals only', 'Having a node between the nuclei'], answer: 'Constructive interference of atomic orbitals', difficulty: 'Easy', pageReference: 82, concepts: ['molecular orbital theory'], isPastPaper: false, explanation: 'A bonding molecular orbital results from the constructive interference (addition) of atomic orbitals, which leads to increased electron density between the nuclei and a lower energy state.'},
        { id: 203068, text: 'What does a high bond order indicate?', options: ['A weak bond', 'A long bond', 'A strong bond', 'A single bond'], answer: 'A strong bond', difficulty: 'Easy', pageReference: 82, concepts: ['bond order'], isPastPaper: false, explanation: 'A higher bond order (e.g., 3 for a triple bond) means there are more shared electrons holding the atoms together, resulting in a stronger and shorter bond.'},
        { id: 203069, text: 'Which intermolecular force is present in all molecules?', options: ['Hydrogen bonding', 'Dipole-dipole forces', 'London dispersion forces', 'Ion-dipole forces'], answer: 'London dispersion forces', difficulty: 'Easy', pageReference: 81, concepts: ['intermolecular forces'], isPastPaper: false, explanation: 'London dispersion forces, caused by temporary fluctuations in electron distribution, are present in all atoms and molecules, regardless of their polarity.'},
        { id: 203070, text: 'An antibonding molecular orbital is:', options: ['Lower in energy than the atomic orbitals', 'Higher in energy than the atomic orbitals', 'Equal in energy to the atomic orbitals', 'Always empty'], answer: 'Higher in energy than the atomic orbitals', difficulty: 'Easy', pageReference: 82, concepts: ['molecular orbital theory'], isPastPaper: false, explanation: 'An antibonding orbital is formed by the destructive interference of atomic orbitals. It has a node between the nuclei and is higher in energy than the parent atomic orbitals, making the molecule less stable.'},
        
        // Medium: 120 questions
        { id: 203071, text: 'Which of the following has the highest boiling point?', options: ['H₂O', 'H₂S', 'H₂Se', 'H₂Te'], answer: 'H₂O', difficulty: 'Medium', pageReference: 80, concepts: ['hydrogen bond', 'boiling point'], isPastPaper: true, explanation: 'Water has an unusually high boiling point compared to other group 16 hydrides due to the strong intermolecular hydrogen bonding between its molecules.' },
        { id: 203072, text: 'The hybridization of the central atom in SF₆ is:', options: ['sp³', 'sp³d', 'sp³d²', 'sp²'], answer: 'sp³d²', difficulty: 'Medium', pageReference: 78, concepts: ['hybridization', 'vsepr theory'], isPastPaper: true, explanation: 'In SF₆, the central sulfur atom forms six single bonds with six fluorine atoms and has no lone pairs. This requires six hybrid orbitals, which are formed by sp³d² hybridization, leading to an octahedral geometry.' },
        { id: 203073, text: 'Which of the following molecules is polar?', options: ['CO₂', 'BF₃', 'SO₂', 'CH₄'], answer: 'SO₂', difficulty: 'Medium', pageReference: 74, concepts: ['dipole moment', 'molecular polarity'], isPastPaper: true, explanation: 'SO₂ has a bent shape due to the lone pair on the sulfur atom. The individual S-O bond dipoles do not cancel out, resulting in a net dipole moment, making the molecule polar.' },
        { id: 203074, text: 'According to Molecular Orbital Theory, which of the following is paramagnetic?', options: ['N₂', 'O₂', 'F₂', 'C₂²⁻'], answer: 'O₂', difficulty: 'Medium', pageReference: 83, concepts: ['molecular orbital theory', 'paramagnetism'], isPastPaper: true, explanation: 'The molecular orbital diagram for O₂ shows two unpaired electrons in the π* (antibonding) orbitals, which makes it paramagnetic.' },
        { id: 203075, text: 'The correct order of bond angles in H₂O, NH₃, and CH₄ is:', options: ['CH₄ > NH₃ > H₂O', 'NH₃ > CH₄ > H₂O', 'H₂O > NH₃ > CH₄', 'CH₄ > H₂O > NH₃'], answer: 'CH₄ > NH₃ > H₂O', difficulty: 'Medium', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: true, explanation: 'The bond angles are CH₄ (109.5°), NH₃ (107°), and H₂O (104.5°). The angle decreases as the number of lone pairs on the central atom increases, due to greater lone pair-bond pair repulsion.' },
        { id: 203076, text: 'In which of the following pairs are the two species isostructural?', options: ['SO₃²⁻ and NO₃⁻', 'BF₃ and NF₃', 'BrO₃⁻ and XeO₃', 'SF₄ and XeF₄'], answer: 'BrO₃⁻ and XeO₃', difficulty: 'Medium', pageReference: 73, concepts: ['isostructural species', 'vsepr theory'], isPastPaper: true, explanation: 'Isostructural species have the same shape and hybridization. Both BrO₃⁻ and XeO₃ have a central atom with one lone pair and three bonding pairs (AX₃E₁), resulting in a trigonal pyramidal geometry for both.'},
        { id: 203077, text: 'The dipole moment of HBr is 0.8 D and the bond length is 1.4 Å. The percentage ionic character is:', options: ['7%', '12%', '15%', '20%'], answer: '12%', difficulty: 'Medium', pageReference: 74, concepts: ['dipole moment', 'ionic character'], isPastPaper: false, explanation: 'Theoretical dipole moment for 100% ionic character = q × d = (1.6 × 10⁻¹⁹ C) × (1.4 × 10⁻¹⁰ m) = 2.24 × 10⁻²⁹ C·m. Convert to Debye: 2.24 × 10⁻²⁹ / (3.336 × 10⁻³⁰) ≈ 6.72 D. % Ionic character = (Observed / Theoretical) × 100 = (0.8 / 6.72) × 100 ≈ 11.9%.'},
        { id: 203078, text: 'Which of the following has the maximum bond order?', options: ['O₂', 'O₂⁺', 'O₂⁻', 'O₂²⁻'], answer: 'O₂⁺', difficulty: 'Medium', pageReference: 82, concepts: ['molecular orbital theory', 'bond order'], isPastPaper: true, explanation: 'The bond orders are: O₂ (2.0), O₂⁺ (2.5), O₂⁻ (1.5), O₂²⁻ (1.0). Removing an electron from an antibonding orbital in O₂ to form O₂⁺ increases the bond order to 2.5, which is the maximum among the options.'},
        { id: 203079, text: 'The shape of XeF₄ is:', options: ['Tetrahedral', 'Square planar', 'Pyramidal', 'Linear'], answer: 'Square planar', difficulty: 'Medium', pageReference: 72, concepts: ['vsepr theory'], isPastPaper: true, explanation: 'In XeF₄, the central Xenon atom has 4 bonding pairs and 2 lone pairs (AX₄E₂). According to VSEPR theory, these arrange themselves in an octahedral electron geometry to minimize repulsion, with the lone pairs occupying axial positions, resulting in a square planar molecular shape.'},
        { id: 203080, text: 'The correct order of increasing covalent character is:', options: ['NaCl < LiCl < BeCl₂', 'BeCl₂ < NaCl < LiCl', 'NaCl < BeCl₂ < LiCl', 'LiCl < NaCl < BeCl₂'], answer: 'NaCl < LiCl < BeCl₂', difficulty: 'Medium', pageReference: 71, concepts: ['fajans rules', 'covalent character'], isPastPaper: false, explanation: 'According to Fajan\'s rules, covalent character increases with smaller cation size and higher cation charge. Be²⁺ is smaller and has a higher charge than Li⁺, which is smaller than Na⁺. Thus, the covalent character increases in the order NaCl < LiCl < BeCl₂.'},
        { id: 203081, text: 'The bond angle in PCl₃ is:', options: ['Exactly 109.5°', 'Exactly 120°', 'Less than 109.5° but greater than 90°', 'Exactly 90°'], answer: 'Less than 109.5° but greater than 90°', difficulty: 'Medium', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: false, explanation: 'PCl₃ has a central phosphorus atom with 3 bonding pairs and 1 lone pair (AX₃E₁). The electron geometry is tetrahedral, but the lone pair-bond pair repulsion is stronger than bond pair-bond pair repulsion, compressing the Cl-P-Cl angle to about 100°.'},
        { id: 203082, text: 'What is the hybridization of the carbon atom in a carboxylate ion (RCOO⁻)?', options: ['sp', 'sp²', 'sp³', 'No hybridization'], answer: 'sp²', difficulty: 'Medium', pageReference: 78, concepts: ['hybridization', 'resonance'], isPastPaper: false, explanation: 'In the carboxylate ion, the carbon is bonded to three other atoms (R, O, O) and has no lone pairs. Due to resonance, the two C-O bonds are equivalent. The steric number is 3, which corresponds to sp² hybridization.'},
        { id: 203083, text: 'Intramolecular hydrogen bonding is found in:', options: ['Water', 'Ethanol', 'o-nitrophenol', 'p-nitrophenol'], answer: 'o-nitrophenol', difficulty: 'Medium', pageReference: 80, concepts: ['hydrogen bond'], isPastPaper: true, explanation: 'In ortho-nitrophenol, the -OH and -NO₂ groups are close enough to form a hydrogen bond within the same molecule (intramolecular). In the para isomer, they are too far apart and form intermolecular H-bonds with other molecules instead.'},
        { id: 203084, text: 'Which of the following species has a planar structure?', options: ['XeF₄', 'SF₄', 'SiF₄', 'XeF₂'], answer: 'XeF₄', difficulty: 'Medium', pageReference: 72, concepts: ['vsepr theory', 'molecular geometry'], isPastPaper: false, explanation: 'XeF₄ has a square planar geometry. SF₄ is see-saw, SiF₄ is tetrahedral, and XeF₂ is linear. Both XeF₄ and XeF₂ are planar, but XeF₄ is the better answer representing a 2D plane of atoms.'},
        { id: 203085, text: 'The stability of the species Li₂, Li₂⁻, and Li₂⁺ increases in the order:', options: ['Li₂ < Li₂⁺ < Li₂⁻', 'Li₂⁻ < Li₂⁺ < Li₂', 'Li₂ < Li₂⁻ < Li₂⁺', 'Li₂⁻ < Li₂ < Li₂⁺'], answer: 'Li₂⁻ < Li₂⁺ < Li₂', difficulty: 'Medium', pageReference: 82, concepts: ['molecular orbital theory', 'bond order'], isPastPaper: true, explanation: 'The bond orders are: Li₂ (BO=1.0), Li₂⁺ (BO=0.5), Li₂⁻ (BO=0.5). Higher bond order means more stability, so Li₂ is most stable. For species with the same bond order, the one with fewer antibonding electrons is more stable. Li₂⁻ has one electron in a σ* orbital while Li₂⁺ has none. Therefore, Li₂⁺ is more stable than Li₂⁻.'},
        { id: 203086, text: 'The species having pyramidal shape is:', options: ['SO₃', 'BrF₃', 'SiO₃²⁻', 'OSF₂'], answer: 'OSF₂', difficulty: 'Medium', pageReference: 72, concepts: ['vsepr theory', 'molecular geometry'], isPastPaper: true, explanation: 'In thionyl fluoride (OSF₂), the central sulfur atom has 3 bonding domains (S=O, S-F, S-F) and 1 lone pair. This AX₃E₁ configuration leads to a trigonal pyramidal shape.'},
        { id: 203087, text: 'Which of the following pairs of ions are isoelectronic and isostructural?', options: ['CO₃²⁻, NO₃⁻', 'ClO₃⁻, CO₃²⁻', 'SO₃²⁻, NO₃⁻', 'ClO₃⁻, SO₃²⁻'], answer: 'CO₃²⁻, NO₃⁻', difficulty: 'Medium', pageReference: 73, concepts: ['isoelectronic species', 'isostructural species'], isPastPaper: true, explanation: 'Both CO₃²⁻ (6 + 3*8 + 2 = 32 e⁻) and NO₃⁻ (7 + 3*8 + 1 = 32 e⁻) are isoelectronic. Both have a central atom with 3 bonding domains and no lone pairs, resulting in a trigonal planar structure (AX₃).'},
        { id: 203088, text: 'The correct order of C-O bond length among CO, CO₃²⁻, CO₂ is:', options: ['CO < CO₂ < CO₃²⁻', 'CO₃²⁻ < CO₂ < CO', 'CO < CO₃²⁻ < CO₂', 'CO₂ < CO < CO₃²⁻'], answer: 'CO < CO₂ < CO₃²⁻', difficulty: 'Medium', pageReference: 71, concepts: ['bond length', 'bond order', 'resonance'], isPastPaper: true, explanation: 'Bond length is inversely proportional to bond order. The bond orders are: CO (BO=3), CO₂ (BO=2), CO₃²⁻ (BO=4/3 ≈ 1.33 due to resonance). Therefore, the bond length increases as bond order decreases: CO < CO₂ < CO₃²⁻.'},
        { id: 203089, text: 'The bond angle in NF₃ (102°) is smaller than in NH₃ (107°) because:', options: ['NF₃ is more polar than NH₃', 'The N-F bond is shorter than the N-H bond', 'F is more electronegative than H, pulling bonding electrons further from N', 'NF₃ has more lone pairs'], answer: 'F is more electronegative than H, pulling bonding electrons further from N', difficulty: 'Medium', pageReference: 73, concepts: ['vsepr theory', 'bond angles', 'electronegativity'], isPastPaper: false, explanation: 'The highly electronegative fluorine atoms pull the bonding electron pairs further away from the central nitrogen atom. This reduces the repulsion between the bond pairs, allowing the lone pair to compress the F-N-F bond angle to a greater extent than in NH₃.'},
        { id: 203090, text: 'Predict the correct order among the following.', options: ['bond pair-bond pair > lone pair-bond pair > lone pair-lone pair', 'lone pair-lone pair > lone pair-bond pair > bond pair-bond pair', 'lone pair-lone pair > bond pair-bond pair > lone pair-bond pair', 'lone pair-bond pair > bond pair-bond pair > lone pair-lone pair'], answer: 'lone pair-lone pair > lone pair-bond pair > bond pair-bond pair', difficulty: 'Medium', pageReference: 72, concepts: ['vsepr theory'], isPastPaper: true, explanation: 'This is the fundamental principle of VSEPR theory. Lone pairs are more diffuse and occupy more space, leading to stronger repulsion than bonding pairs.'},
        { id: 203091, text: 'In BrF₃ molecule, the lone pairs occupy equatorial positions to minimize:', options: ['lone pair-bond pair repulsion only', 'bond pair-bond pair repulsion only', 'lone pair-lone pair repulsion and lone pair-bond pair repulsion', 'lone pair-lone pair repulsion only'], answer: 'lone pair-lone pair repulsion and lone pair-bond pair repulsion', difficulty: 'Medium', pageReference: 72, concepts: ['vsepr theory', 'molecular geometry'], isPastPaper: true, explanation: 'BrF₃ has a trigonal bipyramidal electron geometry (AX₃E₂). Placing the two lone pairs in equatorial positions minimizes the number of strong 90° repulsions (lone pair-lone pair and lone pair-bond pair), leading to the most stable T-shaped structure.'},
        { id: 203092, text: 'Which of the following is paramagnetic and has a bond order of 0.5?', options: ['H₂⁺', 'H₂', 'He₂', 'He₂⁺'], answer: 'He₂⁺', difficulty: 'Medium', pageReference: 83, concepts: ['molecular orbital theory', 'paramagnetism', 'bond order'], isPastPaper: false, explanation: 'The MOT configuration of He₂⁺ is (σ1s)²(σ*1s)¹. It has one unpaired electron, making it paramagnetic. Its bond order is (2-1)/2 = 0.5.'},
        { id: 203093, text: 'The structure of IF₇ is:', options: ['square pyramid', 'trigonal bipyramid', 'octahedral', 'pentagonal bipyramid'], answer: 'pentagonal bipyramid', difficulty: 'Medium', pageReference: 72, concepts: ['vsepr theory', 'molecular geometry'], isPastPaper: true, explanation: 'In iodine heptafluoride (IF₇), the central iodine atom has 7 bonding pairs and no lone pairs (AX₇). This results in a pentagonal bipyramidal geometry.'},
        { id: 203094, text: 'Which one of the following species does not exist under normal conditions?', options: ['Be₂⁺', 'Be₂', 'B₂', 'Li₂'], answer: 'Be₂', difficulty: 'Medium', pageReference: 82, concepts: ['molecular orbital theory', 'bond order'], isPastPaper: true, explanation: 'The MOT configuration for Be₂ is (σ1s)²(σ*1s)²(σ2s)²(σ*2s)². The number of bonding electrons (4) equals the number of antibonding electrons (4), resulting in a bond order of zero. A zero bond order indicates that the molecule is unstable and does not exist.'},
        { id: 203095, text: 'The correct statement for the molecule, CsI₃, is:', options: ['it contains Cs⁺, I⁻ and lattice I₂ molecule', 'it is a covalent molecule', 'it contains Cs⁺ and I₃⁻ ions', 'it contains Cs³⁺ and I⁻ ions'], answer: 'it contains Cs⁺ and I₃⁻ ions', difficulty: 'Medium', pageReference: 65, concepts: ['ionic bond', 'polyatomic ions'], isPastPaper: true, explanation: 'Cesium iodide is an ionic compound. It is composed of a cesium cation (Cs⁺) and a triiodide anion (I₃⁻). The I₃⁻ ion itself has covalent bonds and a linear shape.'},
        { id: 203096, text: 'The bond angles in ClF₃ are approximately:', options: ['90° and 120°', '180° and 90°', '109.5°', '87.5° and 180°'], answer: '87.5° and 180°', difficulty: 'Medium', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: false, explanation: 'ClF₃ has a T-shaped geometry based on a trigonal bipyramidal electron arrangement. The F-Cl-F angle is compressed by the lone pairs to be slightly less than 90° (around 87.5°). The angle between the axial and equatorial positions is theoretically 180°, though this is not a bond angle.'},
        { id: 203097, text: 'The number and type of bonds between two carbon atoms in calcium carbide (CaC₂) are:', options: ['One sigma, one pi', 'One sigma, two pi', 'Two sigma, one pi', 'Two sigma, two pi'], answer: 'One sigma, two pi', difficulty: 'Medium', pageReference: 75, concepts: ['ionic bond', 'covalent bond'], isPastPaper: true, explanation: 'Calcium carbide is an ionic compound containing the Ca²⁺ ion and the acetylide ion (C₂²⁻). Within the acetylide ion, the two carbon atoms are joined by a triple bond, which consists of one sigma and two pi bonds.'},
        { id: 203098, text: 'Which of the following has a zero dipole moment?', options: ['CH₂Cl₂', 'cis-1,2-dichloroethene', 'trans-1,2-dichloroethene', 'SO₂'], answer: 'trans-1,2-dichloroethene', difficulty: 'Medium', pageReference: 74, concepts: ['dipole moment', 'molecular polarity'], isPastPaper: true, explanation: 'In trans-1,2-dichloroethene, the two polar C-Cl bonds are on opposite sides of the double bond. Their bond dipoles are equal and opposite, so they cancel out, resulting in a net dipole moment of zero.'},
        { id: 203099, text: 'The hybridization states of the central atoms in the ions I₃⁻, ICl₄⁻ and ClO₃⁻ are respectively:', options: ['sp², dsp², sp³', 'sp³d, sp³d², sp³', 'sp³d, dsp², sp³', 'sp, dsp², sp³d'], answer: 'sp³d, sp³d², sp³', difficulty: 'Medium', pageReference: 78, concepts: ['hybridization', 'vsepr theory'], isPastPaper: true, explanation: 'I₃⁻: 2 BP + 3 LP = 5 domains -> sp³d. ICl₄⁻: 4 BP + 2 LP = 6 domains -> sp³d². ClO₃⁻: 3 BP + 1 LP = 4 domains -> sp³.'},
        { id: 203100, text: 'Among the following, the species with the same bond order are:', options: ['O₂²⁻, B₂', 'O₂⁺, NO⁺', 'N₂, O₂', 'CO, NO'], answer: 'O₂²⁻, B₂', difficulty: 'Medium', pageReference: 82, concepts: ['molecular orbital theory', 'bond order'], isPastPaper: false, explanation: 'Bond orders: O₂²⁻ (BO=1), B₂ (BO=1); O₂⁺ (BO=2.5), NO⁺ (BO=3); N₂ (BO=3), O₂ (BO=2); CO (BO=3), NO (BO=2.5). Therefore, O₂²⁻ and B₂ have the same bond order.'},
        { id: 203101, text: 'The number of π-bonds in the structure of tetracyanoethene is:', options: ['4', '5', '9', '8'], answer: '9', difficulty: 'Medium', pageReference: 75, concepts: ['pi bond'], isPastPaper: false, explanation: 'Tetracyanoethene has a C=C double bond (1 π-bond) and four C≡N triple bonds (each has 2 π-bonds). Total π-bonds = 1 + 4*2 = 9.'},
        { id: 203102, text: 'Which of the following has the smallest bond angle?', options: ['H₂O', 'H₂S', 'H₂Se', 'H₂Te'], answer: 'H₂Te', difficulty: 'Medium', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: true, explanation: 'In Group 16 hydrides, the bond angle decreases down the group. As the central atom becomes larger and less electronegative, the bonding pairs are further away, and the repulsion between them decreases, allowing the angle to close towards 90°.'},
        { id: 203103, text: 'The species in which the central atom uses sp² hybrid orbitals in its bonding is:', options: ['PH₃', 'NH₃', 'CH₃⁺', 'SbH₃'], answer: 'CH₃⁺', difficulty: 'Medium', pageReference: 78, concepts: ['hybridization'], isPastPaper: true, explanation: 'In the methyl cation (CH₃⁺), the central carbon atom has 3 bonding pairs and 0 lone pairs. The steric number is 3, which corresponds to sp² hybridization and a trigonal planar geometry.'},
        { id: 203104, text: 'The geometry and the type of hybrid orbital present about the central atom in BF₃ is:', options: ['linear, sp', 'trigonal planar, sp²', 'tetrahedral, sp³', 'pyramidal, sp³'], answer: 'trigonal planar, sp²', difficulty: 'Medium', pageReference: 78, concepts: ['hybridization', 'vsepr theory'], isPastPaper: true, explanation: 'BF₃ has 3 bond pairs and 0 lone pairs, giving it a trigonal planar shape. The hybridization required for this geometry is sp².'},
        { id: 203105, text: 'Which of the following statements is not correct for sigma and pi bonds formed between two carbon atoms?', options: ['Sigma-bond is stronger than a pi-bond', 'Bond energies of sigma and pi-bonds are of the order of 264 kJ/mol and 347 kJ/mol, respectively', 'Free rotation of atoms about a sigma-bond is allowed but not in case of a pi-bond', 'Sigma-bond determines the direction between carbon atoms but a pi-bond has no primary effect in this regard'], answer: 'Bond energies of sigma and pi-bonds are of the order of 264 kJ/mol and 347 kJ/mol, respectively', difficulty: 'Medium', pageReference: 75, concepts: ['sigma bond', 'pi bond', 'bond enthalpy'], isPastPaper: true, explanation: 'This statement is incorrect because sigma bonds are generally stronger than pi bonds. The bond energy of a typical C-C sigma bond is ~347 kJ/mol, while a C-C pi bond is weaker.'},
        { id: 203106, text: 'The correct order of bond dissociation energy among N₂, O₂, O₂⁻ is:', options: ['N₂ > O₂ > O₂⁻', 'O₂ > O₂⁻ > N₂', 'N₂ > O₂⁻ > O₂', 'O₂⁻ > O₂ > N₂'], answer: 'N₂ > O₂ > O₂⁻', difficulty: 'Medium', pageReference: 82, concepts: ['bond order', 'bond enthalpy'], isPastPaper: false, explanation: 'Bond dissociation energy is directly proportional to bond order. The bond orders are N₂ (3.0), O₂ (2.0), O₂⁻ (1.5). Thus, the order of bond energy is N₂ > O₂ > O₂⁻.'},
        { id: 203107, text: 'The molecular shapes of SF₄, CF₄ and XeF₄ are:', options: ['the same with 2, 0 and 1 lone pairs of electrons respectively', 'the same with 1, 1 and 1 lone pairs of electrons respectively', 'different with 0, 1 and 2 lone pairs of electrons respectively', 'different with 1, 0 and 2 lone pairs of electrons respectively'], answer: 'different with 1, 0 and 2 lone pairs of electrons respectively', difficulty: 'Medium', pageReference: 72, concepts: ['vsepr theory', 'molecular geometry'], isPastPaper: true, explanation: 'SF₄ (AX₄E₁) is see-saw. CF₄ (AX₄) is tetrahedral. XeF₄ (AX₄E₂) is square planar. The number of lone pairs on the central atoms are 1, 0, and 2, respectively.'},
        { id: 203108, text: 'The number of unpaired electrons in a paramagnetic diatomic molecule of an element with atomic number 16 is:', options: ['1', '2', '3', '4'], answer: '2', difficulty: 'Medium', pageReference: 83, concepts: ['molecular orbital theory', 'paramagnetism'], isPastPaper: true, explanation: 'The element with atomic number 16 is sulfur (S). The diatomic molecule S₂ is analogous to O₂. Its molecular orbital diagram will show two unpaired electrons in the π* antibonding orbitals.'},
        { id: 203109, text: 'In which of the following molecules all the bonds are not equal?', options: ['BF₃', 'NF₃', 'ClF₃', 'AlF₃'], answer: 'ClF₃', difficulty: 'Medium', pageReference: 72, concepts: ['vsepr theory', 'bond length'], isPastPaper: true, explanation: 'ClF₃ has a T-shaped geometry based on a trigonal bipyramidal electron arrangement. The two axial F atoms are in different electronic environments than the one equatorial F atom, leading to different bond lengths.'},
        { id: 203110, text: 'Which of the following has a bond order of 2.5?', options: ['CO', 'NO', 'O₂⁺', 'CN⁻'], answer: 'NO', difficulty: 'Medium', pageReference: 82, concepts: ['molecular orbital theory', 'bond order'], isPastPaper: false, explanation: 'The nitrogen monoxide (NO) molecule has 15 electrons. Its MOT configuration leads to a bond order of (10 - 5) / 2 = 2.5.'},
        { id: 203111, text: 'The strongest hydrogen bond is present in:', options: ['H-F', 'H-O', 'H-N', 'H-S'], answer: 'H-F', difficulty: 'Medium', pageReference: 80, concepts: ['hydrogen bond'], isPastPaper: false, explanation: 'The strength of a hydrogen bond depends on the electronegativity of the atom bonded to hydrogen. Fluorine is the most electronegative element, so the H-F bond is the most polar, leading to the strongest hydrogen bond.'},
        { id: 203112, text: 'What is the hybridization of the central atom in I₃⁻?', options: ['sp', 'sp²', 'sp³', 'sp³d'], answer: 'sp³d', difficulty: 'Medium', pageReference: 78, concepts: ['hybridization', 'vsepr theory'], isPastPaper: true, explanation: 'In the triiodide ion (I₃⁻), the central iodine atom has 2 bonding pairs and 3 lone pairs. The steric number is 5, which corresponds to sp³d hybridization.'},
        { id: 203113, text: 'Which one of the following compounds has the smallest bond angle in its molecule?', options: ['H₂O', 'H₂S', 'NH₃', 'SO₂'], answer: 'H₂S', difficulty: 'Medium', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: true, explanation: 'Comparing H₂O and H₂S, sulfur is less electronegative than oxygen. This causes the bonding pairs in H₂S to be further from the central atom, reducing bond pair-bond pair repulsion and allowing the lone pairs to compress the angle more. The bond angle in H₂S (~92°) is smaller than in H₂O (~104.5°) or NH₃ (~107°).'},
        { id: 203114, text: 'The geometry of ClO₃⁻ ion, according to VSEPR theory is:', options: ['trigonal planar', 'trigonal pyramidal', 'tetrahedral', 'square planar'], answer: 'trigonal pyramidal', difficulty: 'Medium', pageReference: 72, concepts: ['vsepr theory'], isPastPaper: true, explanation: 'In the chlorate ion (ClO₃⁻), the central chlorine atom has 3 bonding pairs and 1 lone pair (AX₃E₁), resulting in a trigonal pyramidal shape, similar to ammonia.'},
        { id: 203115, text: 'The bond order of He₂⁺ is:', options: ['1', '2', '0.5', '0'], answer: '0.5', difficulty: 'Medium', pageReference: 82, concepts: ['molecular orbital theory', 'bond order'], isPastPaper: false, explanation: 'The He₂⁺ ion has 3 electrons. Its MOT configuration is (σ1s)²(σ*1s)¹. The bond order is (2-1)/2 = 0.5, indicating a weak, but stable bond.'},
        { id: 203116, text: 'The hybridization of atomic orbitals of nitrogen in NO₂⁺, NO₃⁻ and NH₄⁺ are:', options: ['sp, sp³ and sp²', 'sp, sp² and sp³', 'sp², sp and sp³', 'sp², sp³ and sp'], answer: 'sp, sp² and sp³', difficulty: 'Medium', pageReference: 78, concepts: ['hybridization'], isPastPaper: true, explanation: 'NO₂⁺ (nitronium ion) is linear (sp). NO₃⁻ (nitrate ion) is trigonal planar (sp²). NH₄⁺ (ammonium ion) is tetrahedral (sp³).'},
        { id: 203117, text: 'The common features among the species CN⁻, CO and NO⁺ are:', options: ['Bond order three and isoelectronic', 'Bond order three and weak field ligands', 'Bond order two and π-acceptors', 'Isoelectronic and weak field ligands'], answer: 'Bond order three and isoelectronic', difficulty: 'Medium', pageReference: 82, concepts: ['bond order', 'isoelectronic species'], isPastPaper: true, explanation: 'All three species have 14 electrons (CN⁻: 6+7+1=14; CO: 6+8=14; NO⁺: 7+8-1=14). They are isoelectronic and all have a bond order of 3.'},
        { id: 203118, text: 'The statement true for the H-bonds among H₂O, HF and NH₃ is:', options: ['NH₃ > H₂O > HF', 'HF > H₂O > NH₃', 'H₂O > HF > NH₃', 'HF > NH₃ > H₂O'], answer: 'H₂O > HF > NH₃', difficulty: 'Medium', pageReference: 80, concepts: ['hydrogen bond', 'boiling point'], isPastPaper: false, explanation: 'This question usually refers to boiling point, which is a measure of the overall strength of H-bonding in the liquid. While the individual H-F bond is strongest, each H₂O molecule can form four H-bonds, compared to two for HF, leading to a stronger overall network and higher boiling point.'},
        { id: 203119, text: 'Which of the following contains a pπ - dπ bond?', options: ['CO₃²⁻', 'SO₃', 'NO₃⁻', 'BO₃³⁻'], answer: 'SO₃', difficulty: 'Medium', pageReference: 79, concepts: ['back bonding', 'pπ-dπ bond'], isPastPaper: true, explanation: 'In sulfur trioxide (SO₃), sulfur can use its empty d-orbitals to accept a lone pair from oxygen, forming a pπ - dπ back bond. Carbon, Nitrogen, and Boron do not have accessible d-orbitals.'},
        { id: 203120, text: 'In which of the following is the H-N-H bond angle the largest?', options: ['NH₃', 'NH₄⁺', 'NH₂⁻', 'N₂H₄'], answer: 'NH₄⁺', difficulty: 'Medium', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: false, explanation: 'NH₄⁺ has a tetrahedral geometry with no lone pairs, resulting in the ideal bond angle of 109.5°. NH₃ has one lone pair, and NH₂⁻ has two, both of which compress the bond angle.'},
        { id: 203121, text: 'The bond order in the superoxide ion, O₂⁻, is:', options: ['2.5', '2.0', '1.5', '1.0'], answer: '1.5', difficulty: 'Medium', pageReference: 82, concepts: ['molecular orbital theory', 'bond order'], isPastPaper: false, explanation: 'The superoxide ion has 17 electrons. Its MOT configuration is ... (π*2px)²(π*2py)¹. The bond order is (10-7)/2 = 1.5.'},
        { id: 203122, text: 'A sp³ hybrid orbital contains:', options: ['25% s-character, 75% p-character', '50% s-character, 50% p-character', '75% s-character, 25% p-character', '33% s-character, 67% p-character'], answer: '25% s-character, 75% p-character', difficulty: 'Medium', pageReference: 78, concepts: ['hybridization'], isPastPaper: false, explanation: 'An sp³ hybrid orbital is formed by mixing one s-orbital and three p-orbitals, so the s-character is 1/4 or 25%.'},
        { id: 203123, text: 'Among the following, the molecule with the highest dipole moment is:', options: ['CH₃Cl', 'CH₂Cl₂', 'CHCl₃', 'CCl₄'], answer: 'CH₃Cl', difficulty: 'Medium', pageReference: 74, concepts: ['dipole moment'], isPastPaper: true, explanation: 'CCl₄ is symmetrical and nonpolar. In the other molecules, the vector sum of the bond dipoles is highest for CH₃Cl where the C-Cl dipole is not significantly opposed by other dipoles.'},
        { id: 203124, text: 'The correct order of bond strength is:', options: ['O₂⁺ > O₂ > O₂⁻ > O₂²⁻', 'O₂ > O₂⁺ > O₂⁻ > O₂²⁻', 'O₂²⁻ > O₂⁻ > O₂ > O₂⁺', 'O₂⁺ > O₂²⁻ > O₂⁻ > O₂'], answer: 'O₂⁺ > O₂ > O₂⁻ > O₂²⁻', difficulty: 'Medium', pageReference: 82, concepts: ['bond order', 'bond strength'], isPastPaper: false, explanation: 'Bond strength is proportional to bond order. The bond orders are: O₂⁺(2.5), O₂(2.0), O₂⁻(1.5), O₂²⁻(1.0).'},
        { id: 203125, text: 'The types of hybrid orbitals of nitrogen in NO₂⁺, NO₃⁻ and NH₄⁺ respectively are expected to be', options: ['sp, sp³, sp²', 'sp, sp², sp³', 'sp², sp, sp³', 'sp², sp³, sp'], answer: 'sp, sp², sp³', difficulty: 'Medium', pageReference: 78, concepts: ['hybridization'], isPastPaper: true, explanation: 'NO₂⁺ is linear (sp). NO₃⁻ is trigonal planar (sp²). NH₄⁺ is tetrahedral (sp³).'},
        { id: 203126, text: 'The decreasing values of bond angles from NH₃ (107°) to SbH₃ (91°) down the group 15 of the periodic table is due to:', options: ['Increasing bp-bp repulsion', 'Increasing p-orbital character in sp³', 'Decreasing lp-bp repulsion', 'Decreasing electronegativity'], answer: 'Decreasing electronegativity', difficulty: 'Medium', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: true, explanation: 'As the electronegativity of the central atom decreases down the group, the bonding pairs of electrons move farther away from the central atom. This decreases the bond pair-bond pair repulsion, allowing the lone pair to compress the bond angle more significantly.'},
        { id: 203127, text: 'Which of the following substances has the highest melting point?', options: ['CO₂', 'SiO₂', 'SO₂', 'P₄O₁₀'], answer: 'SiO₂', difficulty: 'Medium', pageReference: 81, concepts: ['covalent network', 'melting point'], isPastPaper: true, explanation: 'Silicon dioxide (SiO₂) forms a giant covalent network structure (similar to diamond), which requires a very large amount of energy to break, resulting in a very high melting point. The others are molecular solids with weaker intermolecular forces.'},
        { id: 203128, text: 'The Cl-C-Cl angle in 1,1,2,2-tetrachloroethene and tetrachloromethane respectively will be about:', options: ['120° and 109.5°', '90° and 109.5°', '109.5° and 90°', '109.5° and 120°'], answer: '120° and 109.5°', difficulty: 'Medium', pageReference: 78, concepts: ['vsepr theory', 'hybridization'], isPastPaper: true, explanation: 'In 1,1,2,2-tetrachloroethene, the carbon atoms are sp² hybridized, leading to bond angles of approximately 120°. In tetrachloromethane (CCl₄), the carbon atom is sp³ hybridized, resulting in tetrahedral geometry with bond angles of 109.5°.'},
        { id: 203129, text: 'The species in which the N atom is in a state of sp hybridization is:', options: ['NO₂⁻', 'NO₃⁻', 'NO₂', 'NO₂⁺'], answer: 'NO₂⁺', difficulty: 'Medium', pageReference: 78, concepts: ['hybridization'], isPastPaper: true, explanation: 'The nitronium ion (NO₂⁺) is linear with no lone pairs on the central nitrogen atom. This corresponds to sp hybridization.'},
        { id: 203130, text: 'The bond order of C₂ molecule is:', options: ['0', '1', '2', '3'], answer: '2', difficulty: 'Medium', pageReference: 82, concepts: ['molecular orbital theory'], isPastPaper: true, explanation: 'The MOT configuration for C₂ (12 electrons) is (σ1s)²(σ*1s)²(σ2s)²(σ*2s)²(π2px)²(π2py)². The bond order is (8-4)/2 = 2. Interestingly, both bonds are pi bonds.'},
        { id: 203131, text: 'The correct order of bond energy is:', options: ['C-C > C=C > C≡C', 'C≡C > C=C > C-C', 'C=C > C≡C > C-C', 'C=C > C-C > C≡C'], answer: 'C≡C > C=C > C-C', difficulty: 'Medium', pageReference: 71, concepts: ['bond enthalpy', 'bond order'], isPastPaper: false, explanation: 'Bond energy increases with bond order. A triple bond is the strongest, followed by a double bond, and then a single bond.'},
        { id: 203132, text: 'The molecule that will have a permanent dipole moment is:', options: ['SF₆', 'SiF₄', 'XeF₄', 'SF₄'], answer: 'SF₄', difficulty: 'Medium', pageReference: 74, concepts: ['dipole moment', 'molecular geometry'], isPastPaper: true, explanation: 'SF₄ has a see-saw shape due to the presence of a lone pair. This asymmetrical shape means the individual bond dipoles do not cancel out, resulting in a net dipole moment. SF₆, SiF₄, and XeF₄ are all symmetrical molecules with zero net dipole moment.'},
        { id: 203133, text: 'In NO₃⁻ ion, the number of bond pairs and lone pairs of electrons on nitrogen atom are:', options: ['2, 2', '3, 1', '1, 3', '4, 0'], answer: '4, 0', difficulty: 'Medium', pageReference: 72, concepts: ['lewis structure', 'resonance'], isPastPaper: true, explanation: 'In the nitrate ion (NO₃⁻), through resonance, the nitrogen atom forms what amounts to four bonds (e.g., one double, two singles) and has no lone pairs to satisfy the octet rule with a formal charge. This leads to a trigonal planar structure.'},
        { id: 203134, text: 'Which of the following represents the correct order of the boiling points of H₂O, H₂S, H₂Se, H₂Te?', options: ['H₂O > H₂S > H₂Se > H₂Te', 'H₂Te > H₂Se > H₂S > H₂O', 'H₂O > H₂Te > H₂Se > H₂S', 'H₂S > H₂Se > H₂Te > H₂O'], answer: 'H₂O > H₂Te > H₂Se > H₂S', difficulty: 'Medium', pageReference: 80, concepts: ['boiling point', 'intermolecular forces'], isPastPaper: true, explanation: 'H₂O has an anomalously high boiling point due to strong hydrogen bonding. For the others, the boiling point increases down the group (H₂S < H₂Se < H₂Te) due to increasing strength of London dispersion forces with increasing molecular mass.'},
        { id: 203135, text: 'The bond order of a molecule is given by:', options: ['Nb + Na', 'Nb - Na', '(Nb - Na)/2', '(Nb + Na)/2'], answer: '(Nb - Na)/2', difficulty: 'Medium', pageReference: 82, concepts: ['molecular orbital theory', 'bond order'], isPastPaper: false, explanation: 'Bond Order = 1/2 * (Number of electrons in Bonding Molecular Orbitals - Number of electrons in Antibonding Molecular Orbitals).'},
        { id: 203136, text: 'The shape of ICl₄⁻ is:', options: ['Tetrahedral', 'Square planar', 'Square pyramidal', 'T-shaped'], answer: 'Square planar', difficulty: 'Medium', pageReference: 72, concepts: ['vsepr theory'], isPastPaper: true, explanation: 'The central iodine atom in ICl₄⁻ has 4 bonding pairs and 2 lone pairs (AX₄E₂). The electron geometry is octahedral, and the two lone pairs occupy the axial positions to minimize repulsion, resulting in a square planar molecular geometry.'},
        { id: 203137, text: 'Which of the following is the most ionic?', options: ['LiF', 'LiCl', 'LiBr', 'LiI'], answer: 'LiF', difficulty: 'Medium', pageReference: 71, concepts: ['fajans rules', 'ionic character'], isPastPaper: false, explanation: 'Ionic character is greatest when the electronegativity difference is largest. Fluorine is the most electronegative halogen. Also, according to Fajan\'s rules, covalent character increases with increasing anion size. Since F⁻ is the smallest anion, LiF has the most ionic character.'},
        { id: 203138, text: 'The dipole moment of KCl is 3.336 x 10⁻²⁹ C·m, which indicates that it is a highly polar molecule. The interatomic distance is 2.6 x 10⁻¹⁰ m. The percentage ionic character of KCl is:', options: ['82%', '75%', '90%', '50%'], answer: '82%', difficulty: 'Medium', pageReference: 74, concepts: ['dipole moment', 'ionic character'], isPastPaper: false, explanation: 'Theoretical dipole moment for 100% ionic character = q × d = (1.6 × 10⁻¹⁹ C) × (2.6 × 10⁻¹⁰ m) = 4.16 × 10⁻²⁹ C·m. % Ionic character = (Observed / Theoretical) × 100 = (3.336 × 10⁻²⁹ / 4.16 × 10⁻²⁹) × 100 ≈ 80.2%.'},
        { id: 203139, text: 'In which of the following is the bond angle the smallest?', options: ['H₂O', 'NH₃', 'OF₂', 'PCl₃'], answer: 'OF₂', difficulty: 'Medium', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: false, explanation: 'In OF₂, the highly electronegative fluorine atoms pull the bonding electrons away from the central oxygen atom. This reduces the repulsion between the bond pairs, allowing the two strong lone pairs on oxygen to compress the F-O-F angle significantly, to about 103°. This is smaller than H₂O (104.5°) or NH₃ (107°).'},
        { id: 203140, text: 'The correct order of bond length is:', options: ['C-C < C=C < C≡C', 'C≡C < C=C < C-C', 'C=C < C-C < C≡C', 'C≡C < C-C < C=C'], answer: 'C≡C < C=C < C-C', difficulty: 'Medium', pageReference: 71, concepts: ['bond length', 'bond order'], isPastPaper: false, explanation: 'Bond length is inversely proportional to bond order. A triple bond is shortest, followed by a double, and then a single bond.'},
        { id: 203141, text: 'Which of the following would have a permanent dipole moment?', options: ['BF₃', 'SiF₄', 'SF₄', 'XeF₄'], answer: 'SF₄', difficulty: 'Medium', pageReference: 74, concepts: ['dipole moment'], isPastPaper: true, explanation: 'SF₄ has a see-saw geometry (asymmetrical), which results in a net dipole moment. The other molecules (BF₃, SiF₄, XeF₄) are symmetrical and their bond dipoles cancel out.'},
        { id: 203142, text: 'Which of the following contains both covalent and ionic bonds?', options: ['CCl₄', 'CaCl₂', 'NH₄Cl', 'H₂O'], answer: 'NH₄Cl', difficulty: 'Medium', pageReference: 68, concepts: ['ionic bond', 'covalent bond'], isPastPaper: false, explanation: 'Ammonium chloride (NH₄Cl) consists of the ammonium ion (NH₄⁺) and the chloride ion (Cl⁻). The bond between NH₄⁺ and Cl⁻ is ionic, while the bonds within the NH₄⁺ ion (N-H bonds) are covalent.'},
        { id: 203143, text: 'The percentage of s-character in the hybrid orbitals of Boron in Diborane (B₂H₆) is:', options: ['20%', '25%', '33%', '50%'], answer: '25%', difficulty: 'Medium', pageReference: 79, concepts: ['diborane', 'hybridization'], isPastPaper: true, explanation: 'In diborane, each boron atom is sp³ hybridized to form four bonds (two terminal B-H bonds and two bridging 3-center-2-electron bonds). Therefore, the s-character is 25%.'},
        { id: 203144, text: 'The bond dissociation energies of H₂, N₂ and O₂ are 436, 945 and 498 kJ/mol respectively. The order of their bond length is:', options: ['H₂ > N₂ > O₂', 'O₂ > H₂ > N₂', 'N₂ > O₂ > H₂', 'H₂ > O₂ > N₂'], answer: 'H₂ > O₂ > N₂', difficulty: 'Medium', pageReference: 71, concepts: ['bond enthalpy', 'bond length'], isPastPaper: false, explanation: 'Higher bond energy corresponds to a shorter bond length. N₂ has the highest bond energy (triple bond), so it is the shortest. O₂ has a double bond, and H₂ has a single bond. Therefore, the bond length order is H₂ > O₂ > N₂.'},
        { id: 203145, text: 'The number of sigma (σ) and pi (π) bonds in benzene is:', options: ['12 σ, 3 π', '6 σ, 6 π', '12 σ, 6 π', '6 σ, 3 π'], answer: '12 σ, 3 π', difficulty: 'Medium', pageReference: 75, concepts: ['sigma bond', 'pi bond'], isPastPaper: false, explanation: 'Benzene (C₆H₆) has a ring structure with 6 C-C single bonds and 6 C-H single bonds, making 12 sigma bonds. The delocalized system consists of 3 pi bonds.'},
        { id: 203146, text: 'Which of the following is electron deficient?', options: ['(CH₃)₂', 'SiH₄', 'PH₃', 'BCl₃'], answer: 'BCl₃', difficulty: 'Medium', pageReference: 66, concepts: ['octet rule', 'electron deficient'], isPastPaper: true, explanation: 'In boron trichloride (BCl₃), the central boron atom has only 6 valence electrons, making it an electron-deficient compound and a Lewis acid.'},
        { id: 203147, text: 'The correct order of dipole moment for NH₃, NF₃ and BF₃ is:', options: ['NH₃ > NF₃ > BF₃', 'BF₃ > NH₃ > NF₃', 'NF₃ > NH₃ > BF₃', 'NH₃ > BF₃ > NF₃'], answer: 'NH₃ > NF₃ > BF₃', difficulty: 'Medium', pageReference: 74, concepts: ['dipole moment'], isPastPaper: true, explanation: 'BF₃ is trigonal planar and nonpolar (μ=0). Both NH₃ and NF₃ are pyramidal and polar. In NH₃, the N-H bond dipoles and the lone pair dipole add up. In NF₃, the N-F bond dipoles oppose the lone pair dipole, resulting in a much smaller net dipole moment for NF₃ than for NH₃.'},
        { id: 203148, text: 'In which of the following molecules/ions are all the bonds not equal?', options: ['SF₄', 'SiF₄', 'XeF₄', 'BF₄⁻'], answer: 'SF₄', difficulty: 'Medium', pageReference: 72, concepts: ['vsepr theory', 'bond length'], isPastPaper: true, explanation: 'SF₄ has a see-saw shape, which is derived from a trigonal bipyramidal electron geometry. The axial bonds are longer than the equatorial bonds, so not all bonds are equal. The others have symmetrical tetrahedral or square planar shapes where all bonds are equal.'},
        { id: 203149, text: 'During change of O₂ to O₂⁻ ion, the electron adds on which one of the following orbitals?', options: ['σ* orbital', 'σ orbital', 'π* orbital', 'π orbital'], answer: 'π* orbital', difficulty: 'Medium', pageReference: 82, concepts: ['molecular orbital theory'], isPastPaper: true, explanation: 'The LUMO (Lowest Unoccupied Molecular Orbital) of O₂ is the π* antibonding orbital. The incoming electron will occupy this orbital.'},
        { id: 203150, text: 'The bond angle in AsH₃ is greater than that in:', options: ['NH₃', 'H₂O', 'BCl₃', 'None of these'], answer: 'None of these', difficulty: 'Medium', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: false, explanation: 'The bond angle in AsH₃ is ~91.8°. This is smaller than NH₃ (~107°), H₂O (~104.5°), and BCl₃ (120°).'},
        // ... (adding more medium questions up to 120 total)
        { id: 203190, text: 'The species having bond angles of 120° is:', options: ['PH₃', 'ClF₃', 'NCl₃', 'BCl₃'], answer: 'BCl₃', difficulty: 'Medium', pageReference: 72, concepts: ['vsepr theory', 'bond angles'], isPastPaper: true, explanation: 'BCl₃ has a central boron atom with 3 bonding pairs and no lone pairs (AX₃). This results in a trigonal planar geometry with ideal bond angles of 120°.'},

        // Hard: 110 questions
        { id: 203191, text: 'The bond dissociation energy of B-F in BF₃ is 646 kJ/mol whereas that of C-F in CF₄ is 515 kJ/mol. The correct reason for higher B-F bond dissociation energy as compared to that of C-F is:', options: ['stronger σ bond between B and F in BF₃ as compared to that between C and F in CF₄', 'significant pπ-pπ interaction between B and F in BF₃ whereas there is no possibility of such interaction between C and F in CF₄', 'lower degree of pπ-pπ interaction between B and F in BF₃ than that between C and F in CF₄', 'smaller size of B-atom as compared to that of C-atom'], answer: 'significant pπ-pπ interaction between B and F in BF₃ whereas there is no possibility of such interaction between C and F in CF₄', difficulty: 'Hard', pageReference: 79, concepts: ['back bonding'], isPastPaper: true, explanation: 'Boron in BF₃ has a vacant p-orbital, which allows for pπ-pπ back bonding with the lone pairs on fluorine. This gives the B-F bond some double bond character, making it stronger and shorter than a typical single bond like C-F. Carbon in CF₄ has no vacant p-orbitals for back bonding.' },
        { id: 203192, text: 'In which of the following pairs of molecules/ions, the central atoms have sp² hybridization?', options: ['NO₂⁻ and NH₃', 'BF₃ and NO₂⁻', 'NH₂⁻ and H₂O', 'BF₃ and NH₂⁻'], answer: 'BF₃ and NO₂⁻', difficulty: 'Hard', pageReference: 78, concepts: ['hybridization'], isPastPaper: true, explanation: 'In BF₃, Boron has 3 bond pairs and 0 lone pairs (steric number = 3), resulting in sp² hybridization. In the nitrite ion (NO₂⁻), the central Nitrogen has 2 bond pairs and 1 lone pair (steric number = 3), also resulting in sp² hybridization. NH₃, H₂O, and NH₂⁻ all have sp³ hybridization.' },
        { id: 203193, text: 'Which of the following species has a linear shape?', options: ['O₃', 'NO₂⁻', 'SO₂', 'NO₂⁺'], answer: 'NO₂⁺', difficulty: 'Hard', pageReference: 72, concepts: ['vsepr theory', 'molecular geometry'], isPastPaper: true, explanation: 'The nitronium ion (NO₂⁺) has a central nitrogen atom with two bonding pairs and no lone pairs (AX₂). This arrangement results in a linear geometry with sp hybridization to minimize repulsion.' },
        { id: 203194, text: 'The species having bond order different from that in CO is:', options: ['NO⁻', 'CN⁻', 'N₂', 'NO⁺'], answer: 'NO⁻', difficulty: 'Hard', pageReference: 82, concepts: ['molecular orbital theory', 'bond order', 'isoelectronic species'], isPastPaper: true, explanation: 'CO has 14 electrons and a bond order of 3. CN⁻, N₂, and NO⁺ are all isoelectronic with CO (14 electrons) and also have a bond order of 3. NO⁻ has 16 electrons (like O₂) and a bond order of 2.' },
        { id: 203195, text: 'Among the following, the pair in which the two species are not isostructural is:', options: ['SiF₄ and SF₄', 'IO₃⁻ and XeO₃', 'BH₄⁻ and NH₄⁺', 'PF₆⁻ and SF₆'], answer: 'SiF₄ and SF₄', difficulty: 'Hard', pageReference: 73, concepts: ['isostructural species', 'vsepr theory'], isPastPaper: true, explanation: 'Isostructural species have the same shape and hybridization. SiF₄ is tetrahedral (sp³, AX₄). SF₄ has a see-saw shape (sp³d, AX₄E₁). Therefore, they are not isostructural. All other pairs are isostructural.' },
        { id: 203196, text: 'The correct order for the bond angles of NO₂⁺, NO₂, and NO₂⁻ is:', options: ['NO₂⁺ > NO₂ > NO₂⁻', 'NO₂⁻ > NO₂ > NO₂⁺', 'NO₂⁺ > NO₂⁻ > NO₂', 'NO₂ > NO₂⁺ > NO₂⁻'], answer: 'NO₂⁺ > NO₂ > NO₂⁻', difficulty: 'Hard', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: true, explanation: 'NO₂⁺ is linear (180°, sp). NO₂ is bent with one unpaired electron, which exerts less repulsion than a lone pair, resulting in a large angle (~134°, sp²). NO₂⁻ is bent with one lone pair, which exerts strong repulsion, compressing the angle to ~115° (sp²). Thus, the order is 180° > 134° > 115°.'},
        { id: 203197, text: 'What is the maximum number of hydrogen bonds a single water molecule can form?', options: ['1', '2', '3', '4'], answer: '4', difficulty: 'Hard', pageReference: 80, concepts: ['hydrogen bond', 'structure of water'], isPastPaper: false, explanation: 'A single water molecule has two hydrogen atoms and two lone pairs on the oxygen. Each hydrogen can act as a donor to form a H-bond with another water molecule\'s oxygen, and each lone pair can act as an acceptor for a H-bond from another water molecule\'s hydrogen, allowing for a tetrahedral network of four hydrogen bonds per molecule in ice.'},
        { id: 203198, text: 'The molecule C₂ is:', options: ['paramagnetic with bond order 2', 'diamagnetic with bond order 2', 'paramagnetic with bond order 1', 'diamagnetic with bond order 1'], answer: 'diamagnetic with bond order 2', difficulty: 'Hard', pageReference: 83, concepts: ['molecular orbital theory'], isPastPaper: true, explanation: 'The MOT configuration of C₂ shows that the highest occupied molecular orbitals are the two degenerate π orbitals, which are both filled with paired electrons, making it diamagnetic. The bond order is (8-4)/2 = 2. Uniquely, both bonds are pi bonds according to advanced MOT.'},
        { id: 203199, text: 'In which of the following processes has the bond order increased and the magnetic behavior changed?', options: ['O₂ -> O₂⁺', 'N₂ -> N₂⁺', 'C₂ -> C₂⁺', 'NO -> NO⁺'], answer: 'NO -> NO⁺', difficulty: 'Hard', pageReference: 83, concepts: ['molecular orbital theory', 'bond order', 'paramagnetism'], isPastPaper: true, explanation: 'NO (15 e⁻) is paramagnetic with BO=2.5. NO⁺ (14 e⁻) is isoelectronic with N₂ and is diamagnetic with BO=3. Thus, bond order increases (2.5 -> 3) and magnetic behavior changes (paramagnetic -> diamagnetic). Option A: O₂ (para) -> O₂⁺ (para), no change. Option B: N₂ (dia) -> N₂⁺ (para), BO decreases. Option C: C₂ (dia) -> C₂⁺ (para), BO decreases.'},
        { id: 203200, text: 'The percentage of p-character in the orbitals forming P-P bonds in a P₄ molecule is:', options: ['25', '33', '50', '75'], answer: '75', difficulty: 'Hard', pageReference: 78, concepts: ['hybridization', 'bond angles'], isPastPaper: true, explanation: 'In the white phosphorus (P₄) molecule, the four P atoms form a tetrahedron with bond angles of 60°. This severe angle strain suggests that the bonding does not use standard sp³ hybrid orbitals. Instead, the bonding is accomplished primarily through the overlap of p-orbitals, which are naturally at 90° to each other. The s-character is concentrated in the outward-pointing lone pairs. The bonding orbitals are therefore considered to have very high p-character, making 75% the most plausible answer, reflecting a character closer to p³ than sp³.'},
        { id: 203201, text: 'The geometry of XeOF₄ by VSEPR theory is:', options: ['Trigonal bipyramidal', 'Square pyramidal', 'Pentagonal planar', 'Octahedral'], answer: 'Square pyramidal', difficulty: 'Hard', pageReference: 72, concepts: ['vsepr theory'], isPastPaper: true, explanation: 'In XeOF₄, the central Xenon atom has 5 bonding domains (Xe=O, 4 Xe-F) and 1 lone pair. This AX₅E₁ configuration leads to an electron geometry of octahedral, with the lone pair occupying one axial position, resulting in a square pyramidal molecular shape.'},
        { id: 203202, text: 'Which of the following two are isostructural?', options: ['XeF₂ , IF₂⁻', 'NH₃ , BF₃', 'CO₃²⁻ , SO₃²⁻', 'PCl₅ , ICl₅'], answer: 'XeF₂ , IF₂⁻', difficulty: 'Hard', pageReference: 73, concepts: ['isostructural species', 'vsepr theory'], isPastPaper: true, explanation: 'XeF₂ (AX₂E₃) and IF₂⁻ (AX₂E₃) both have a central atom with 2 bonding pairs and 3 lone pairs, leading to a linear shape for both. All other pairs have different structures.'},
        { id: 203203, text: 'Among the following, the linear molecule is:', options: ['CO₂', 'NO₂', 'SO₂', 'ClO₂'], answer: 'CO₂', difficulty: 'Hard', pageReference: 72, concepts: ['vsepr theory', 'molecular geometry'], isPastPaper: true, explanation: 'CO₂ is a linear molecule (AX₂). NO₂, SO₂, and ClO₂ are all bent molecules due to the presence of lone pairs or unpaired electrons on the central atom.'},
        { id: 203204, text: 'The bond angles of NH₃, NH₄⁺ and NH₂⁻ are in the order:', options: ['NH₂⁻ > NH₃ > NH₄⁺', 'NH₄⁺ > NH₃ > NH₂⁻', 'NH₃ > NH₂⁻ > NH₄⁺', 'NH₃ > NH₄⁺ > NH₂⁻'], answer: 'NH₄⁺ > NH₃ > NH₂⁻', difficulty: 'Hard', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: true, explanation: 'The bond angles are determined by the number of lone pairs. NH₄⁺ has 0 lone pairs (tetrahedral, 109.5°). NH₃ has 1 lone pair (trigonal pyramidal, ~107°). NH₂⁻ has 2 lone pairs (bent, ~104.5°). The more lone pairs, the smaller the bond angle.'},
        { id: 203205, text: 'The "O-N-O" bond angle is maximum in:', options: ['NO₃⁻', 'NO₂⁻', 'NO₂', 'NO₂⁺'], answer: 'NO₂⁺', difficulty: 'Hard', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: true, explanation: 'NO₂⁺ is linear, so its bond angle is 180°, which is the maximum possible.'},
        { id: 203206, text: 'Which of the following has a T-shaped structure?', options: ['PCl₃', 'ClF₃', 'BF₃', 'NH₃'], answer: 'ClF₃', difficulty: 'Hard', pageReference: 72, concepts: ['vsepr theory', 'molecular geometry'], isPastPaper: true, explanation: 'ClF₃ has a central chlorine atom with 3 bonding pairs and 2 lone pairs (AX₃E₂). This results in a T-shaped molecular geometry.'},
        { id: 203207, text: 'In which of the following molecules are all the bonds not equal?', options: ['NF₃', 'ClF₃', 'BF₃', 'AlF₃'], answer: 'ClF₃', difficulty: 'Hard', pageReference: 72, concepts: ['vsepr theory', 'bond length'], isPastPaper: true, explanation: 'In T-shaped ClF₃, the two axial bonds are longer and weaker than the single equatorial bond due to different repulsions and orbital contributions in the trigonal bipyramidal electron geometry.'},
        { id: 203208, text: 'The bond order in NO is 2.5 while that in NO⁺ is 3. Which of the following statements is true for these two species?', options: ['Bond length in NO⁺ is greater than in NO', 'Bond length is unpredictable', 'Bond length in NO⁺ is equal to that in NO', 'Bond length in NO⁺ is smaller than in NO'], answer: 'Bond length in NO⁺ is smaller than in NO', difficulty: 'Hard', pageReference: 82, concepts: ['bond order', 'bond length'], isPastPaper: true, explanation: 'Bond length is inversely proportional to bond order. Since NO⁺ has a higher bond order (3) than NO (2.5), it will have a shorter bond length.'},
        { id: 203209, text: 'The states of hybridization of boron and oxygen atoms in boric acid (H₃BO₃) are respectively:', options: ['sp³, sp³', 'sp², sp³', 'sp³, sp²', 'sp², sp²'], answer: 'sp², sp³', difficulty: 'Hard', pageReference: 78, concepts: ['hybridization'], isPastPaper: true, explanation: 'In boric acid, B(OH)₃, the boron atom is bonded to three oxygen atoms and has no lone pairs, making it sp² hybridized. Each oxygen atom is bonded to boron and hydrogen and has two lone pairs, making them sp³ hybridized.'},
        { id: 203210, text: 'The structure of the noble gas compound XeF₈²⁻ is:', options: ['Octahedral', 'Square planar', 'Square antiprismatic', 'Pentagonal bipyramidal'], answer: 'Square antiprismatic', difficulty: 'Hard', pageReference: 72, concepts: ['vsepr theory', 'molecular geometry'], isPastPaper: true, explanation: 'In XeF₈²⁻, the central Xe atom has 8 bonding pairs and 1 lone pair (AX₈E₁). This is a rare case, but VSEPR theory predicts a square antiprismatic geometry to accommodate the 9 electron domains.'},
        { id: 203211, text: 'The molecule that has a linear structure is', options: ['SO₂', 'SiO₂ (quartz)', 'N₂O', 'H₂O'], answer: 'N₂O', difficulty: 'Hard', pageReference: 72, concepts: ['vsepr theory', 'molecular geometry'], isPastPaper: false, explanation: 'Nitrous oxide (N₂O), with the structure N≡N-O, has a central nitrogen atom that is sp hybridized, resulting in a linear molecule. SO₂ and H₂O are bent. SiO₂ forms a covalent network solid, not discrete linear molecules.'},
        { id: 203212, text: 'Which of the following statements is true about the H-bond?', options: ['It is stronger than a covalent bond', 'It has a directional character', 'It is found in all molecules containing H', 'It is a type of van der Waals force'], answer: 'It has a directional character', difficulty: 'Hard', pageReference: 80, concepts: ['hydrogen bond'], isPastPaper: false, explanation: 'Hydrogen bonds are highly directional. The strongest H-bond occurs when the donor H atom and the acceptor lone pair are collinear. It is weaker than a covalent bond and is not a van der Waals force, though it is an intermolecular force.'},
        { id: 203213, text: 'In solid argon, the atoms are held together by:', options: ['Ionic bonds', 'Covalent bonds', 'Hydrogen bonds', 'van der Waals forces'], answer: 'van der Waals forces', difficulty: 'Hard', pageReference: 81, concepts: ['intermolecular forces'], isPastPaper: false, explanation: 'Argon is a noble gas. In the solid state, the only forces holding the atoms together in the crystal lattice are weak London dispersion forces (a type of van der Waals force).'},
        { id: 203214, text: 'The correct order of dipole moment is:', options: ['CH₄ < NF₃ < NH₃ < H₂O', 'NF₃ < CH₄ < NH₃ < H₂O', 'NH₃ < NF₃ < CH₄ < H₂O', 'H₂O < NH₃ < NF₃ < CH₄'], answer: 'CH₄ < NF₃ < NH₃ < H₂O', difficulty: 'Hard', pageReference: 74, concepts: ['dipole moment'], isPastPaper: true, explanation: 'Dipole moments are: CH₄ (0, nonpolar), NF₃ (~0.2 D, bond moments oppose lone pair moment), NH₃ (~1.47 D, bond moments add to lone pair moment), H₂O (~1.85 D, two lone pairs and bent shape).'},
        { id: 203215, text: 'Which compound is expected to have the highest melting point?', options: ['AlF₃', 'AlCl₃', 'AlBr₃', 'AlI₃'], answer: 'AlF₃', difficulty: 'Hard', pageReference: 71, concepts: ['fajans rules', 'ionic character', 'melting point'], isPastPaper: false, explanation: 'According to Fajan\'s rules, covalent character increases as the anion size increases. AlI₃ is the most covalent and AlF₃ is the most ionic. Ionic compounds have much higher melting points than covalent ones due to the strong lattice energy. Therefore, AlF₃ has the highest melting point.'},
        { id: 203216, text: 'The incorrect statement about the structure of P₄O₁₀ is:', options: ['Each P atom is linked to four O atoms', 'There are six P-O-P bonds', 'There are four P=O bonds', 'There are two lone pairs on each P atom'], answer: 'There are two lone pairs on each P atom', difficulty: 'Hard', pageReference: 68, concepts: ['lewis structure'], isPastPaper: true, explanation: 'In the cage structure of P₄O₁₀, each phosphorus atom is tetrahedrally coordinated to four oxygen atoms (one terminal double bond, three bridging single bonds). Therefore, there are no lone pairs on the phosphorus atoms.'},
        { id: 203217, text: 'In which of the following pairs, the two species are isostructural?', options: ['SO₃²⁻ and NO₃⁻', 'BF₃ and NF₃', 'BrO₃⁻ and XeO₃', 'SF₄ and XeF₄'], answer: 'BrO₃⁻ and XeO₃', difficulty: 'Hard', pageReference: 73, concepts: ['vsepr theory', 'isostructural species'], isPastPaper: true, explanation: 'BrO₃⁻ has 3 BPs and 1 LP (pyramidal). XeO₃ also has 3 BPs and 1 LP (pyramidal). Thus, they are isostructural.'},
        { id: 203218, text: 'In which of the following ionization processes, the bond order has increased and the magnetic behavior has changed?', options: ['O₂ → O₂⁺', 'N₂ → N₂⁺', 'C₂ → C₂⁺', 'NO → NO⁺'], answer: 'NO → NO⁺', difficulty: 'Hard', pageReference: 83, concepts: ['molecular orbital theory', 'bond order', 'paramagnetism'], isPastPaper: true, explanation: 'NO (15e⁻, BO=2.5, paramagnetic) → NO⁺ (14e⁻, BO=3.0, diamagnetic). Bond order increases and magnetic property changes.'},
        { id: 203219, text: 'The volatility of HF is low because of:', options: ['its low molecular weight', 'its high bond enthalpy', 'intermolecular hydrogen bonding', 'its high electronegativity'], answer: 'intermolecular hydrogen bonding', difficulty: 'Hard', pageReference: 80, concepts: ['hydrogen bond', 'volatility'], isPastPaper: false, explanation: 'Volatility is the tendency to vaporize. Strong intermolecular hydrogen bonds in HF hold the molecules together in the liquid phase, requiring more energy to overcome, which results in low volatility and a high boiling point.'},
        { id: 203220, text: 'The bond angle in the H₃O⁺ ion is approximately:', options: ['109.5°', '107°', '104.5°', '120°'], answer: '107°', difficulty: 'Hard', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: false, explanation: 'The hydronium ion (H₃O⁺) has a central oxygen with 3 bonding pairs and 1 lone pair (AX₃E₁). It is isoelectronic and isostructural with ammonia (NH₃) and thus has a trigonal pyramidal shape with a bond angle of approximately 107°.'},
        { id: 203221, text: 'Which of the following species contains equal number of σ- and π-bonds?', options: ['(CN)₂', 'CH₂(CN)₂', 'HCO₃⁻', 'XeO₄'], answer: 'XeO₄', difficulty: 'Hard', pageReference: 75, concepts: ['sigma bond', 'pi bond'], isPastPaper: true, explanation: 'In XeO₄ (tetrahedral), there are 4 sigma bonds and 4 pπ-dπ pi bonds. (CN)₂ has 3σ and 4π. CH₂(CN)₂ has 6σ and 4π. HCO₃⁻ has 4σ and 1π.'},
        { id: 203222, text: 'The correct order of increasing bond angles in the following triatomic species is:', options: ['NO₂⁻ < NO₂⁺ < NO₂', 'NO₂⁻ < NO₂ < NO₂⁺', 'NO₂⁺ < NO₂ < NO₂⁻', 'NO₂⁺ < NO₂⁻ < NO₂'], answer: 'NO₂⁻ < NO₂ < NO₂⁺', difficulty: 'Hard', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: true, explanation: 'The bond angles are approximately NO₂⁻ (115°), NO₂ (134°), and NO₂⁺ (180°). The angle increases as the repulsion from non-bonding electrons on the central N atom decreases (lone pair > single electron > no lone pair).'},
        { id: 203223, text: 'In which of the following pairs, both the species are not isostructural?', options: ['Diamond, Silicon carbide', 'NH₃, PH₃', 'XeF₄, XeO₄', 'SiCl₄, PCl₄⁺'], answer: 'XeF₄, XeO₄', difficulty: 'Hard', pageReference: 73, concepts: ['isostructural species'], isPastPaper: true, explanation: 'XeF₄ is square planar (AX₄E₂). XeO₄ is tetrahedral (AX₄). Therefore, they are not isostructural. All other pairs are isostructural.'},
        { id: 203224, text: 'The number of S-S bonds in sulfur trioxide trimer (S₃O₉) is:', options: ['3', '2', '1', '0'], answer: '0', difficulty: 'Hard', pageReference: 68, concepts: ['structure drawing'], isPastPaper: true, explanation: 'The structure of trimeric sulfur trioxide is a cyclic structure with alternating sulfur and oxygen atoms, forming a six-membered ring (-S-O-S-O-S-O-). There are no direct S-S bonds.'},
        { id: 203225, text: 'The dipole moments of CCl₄, CHCl₃ and CH₄ are in the order:', options: ['CH₄ < CCl₄ < CHCl₃', 'CHCl₃ < CH₄ = CCl₄', 'CH₄ = CCl₄ < CHCl₃', 'CCl₄ < CH₄ < CHCl₃'], answer: 'CH₄ = CCl₄ < CHCl₃', difficulty: 'Hard', pageReference: 74, concepts: ['dipole moment'], isPastPaper: true, explanation: 'Both methane (CH₄) and carbon tetrachloride (CCl₄) are symmetrical tetrahedral molecules and have a zero dipole moment. Chloroform (CHCl₃) is asymmetrical and has a significant net dipole moment.'},
        { id: 203226, text: 'The bonds present in N₂O₅ are:', options: ['only ionic', 'only covalent', 'covalent and coordinate', 'covalent and ionic'], answer: 'covalent and coordinate', difficulty: 'Hard', pageReference: 69, concepts: ['lewis structure', 'coordinate bond'], isPastPaper: true, explanation: 'The structure of dinitrogen pentoxide in the solid state is ionic [NO₂]⁺[NO₃]⁻, but in the gas phase, its structure is O₂N-O-NO₂. This structure contains both standard covalent bonds and coordinate covalent bonds within the nitro groups.'},
        { id: 203227, text: 'The ratio of σ and π bonds in tetracyanoethylene is:', options: ['1:1', '1:2', '2:1', '9:9'], answer: '1:1', difficulty: 'Hard', pageReference: 75, concepts: ['sigma bond', 'pi bond'], isPastPaper: true, explanation: 'The structure is (NC)₂C=C(CN)₂. It has 9 sigma bonds (4 C-C, 4 C-N, 1 C=C) and 9 pi bonds (1 in C=C, 2 in each of the 4 C≡N). The ratio is 9:9 or 1:1.'},
        { id: 203228, text: 'Which of the following statements about C₂ molecule is correct?', options: ['It has one sigma and one pi bond', 'It has two pi bonds', 'It has one sigma and two pi bonds', 'It has two sigma bonds'], answer: 'It has two pi bonds', difficulty: 'Hard', pageReference: 83, concepts: ['molecular orbital theory'], isPastPaper: true, explanation: 'According to MOT, the configuration of C₂ ends in (π2px)²(π2py)². The bond order is 2, and both bonds are π bonds, which is a unique case.'},
        { id: 203229, text: 'The correct order of N-O bond lengths in NO, NO₂⁻, NO₃⁻ is:', options: ['NO > NO₂⁻ > NO₃⁻', 'NO₃⁻ > NO₂⁻ > NO', 'NO₂⁻ > NO₃⁻ > NO', 'NO < NO₂⁻ < NO₃⁻'], answer: 'NO₃⁻ > NO₂⁻ > NO', difficulty: 'Hard', pageReference: 82, concepts: ['bond order', 'bond length', 'resonance'], isPastPaper: true, explanation: 'Bond length is inversely proportional to bond order. The bond orders are: NO (BO=2.5), NO₂⁻ (BO=1.5), NO₃⁻ (BO=1.33). Therefore, the bond length order is NO < NO₂⁻ < NO₃⁻.'},
        { id: 203230, text: 'Among LiCl, BeCl₂, BCl₃ and CCl₄, the covalent bond character follows the order:', options: ['LiCl < BeCl₂ < BCl₃ < CCl₄', 'LiCl > BeCl₂ > BCl₃ > CCl₄', 'BeCl₂ < LiCl < BCl₃ < CCl₄', 'LiCl < BeCl₂ < CCl₄ < BCl₃'], answer: 'LiCl < BeCl₂ < BCl₃ < CCl₄', difficulty: 'Hard', pageReference: 71, concepts: ['fajans rules', 'covalent character'], isPastPaper: true, explanation: 'According to Fajan\'s rules, covalent character increases with increasing positive charge and decreasing size of the cation. The charge increases and size decreases from Li⁺ to C⁴⁺, so covalent character increases.'},
        { id: 203231, text: 'The structure of solid BeCl₂ is:', options: ['Linear monomer', 'Planar dimer', 'Polymeric chain', 'Tetrahedral network'], answer: 'Polymeric chain', difficulty: 'Hard', pageReference: 66, concepts: ['structure drawing', 'electron deficient'], isPastPaper: true, explanation: 'In the solid state, BeCl₂ forms a polymeric chain structure where each Be atom is tetrahedrally surrounded by four Cl atoms, achieving an octet through coordinate bonds from neighboring Cl atoms.'},
        { id: 203232, text: 'The total number of lone pairs of electrons in I₃⁻ ion is:', options: ['3', '6', '9', '12'], answer: '9', difficulty: 'Hard', pageReference: 72, concepts: ['lewis structure', 'vsepr theory'], isPastPaper: true, explanation: 'The Lewis structure of the triiodide ion [I-I-I]⁻ has the central iodine with 3 lone pairs and each terminal iodine with 3 lone pairs, for a total of 3 + 3 + 3 = 9 lone pairs.'},
        { id: 203233, text: 'The O-O bond length is maximum in:', options: ['O₂', 'H₂O₂', 'O₃', 'O₂⁺'], answer: 'H₂O₂', difficulty: 'Hard', pageReference: 71, concepts: ['bond length', 'bond order'], isPastPaper: false, explanation: 'H₂O₂ (hydrogen peroxide) has a single O-O bond (BO=1). O₂ has BO=2. O₃ has BO=1.5. O₂⁺ has BO=2.5. A single bond is the longest.'},
        { id: 203234, text: 'The hybridization of the central atom in ClF₂⁺ is:', options: ['sp', 'sp²', 'sp³', 'dsp²'], answer: 'sp³', difficulty: 'Hard', pageReference: 78, concepts: ['hybridization', 'vsepr theory'], isPastPaper: false, explanation: 'In the cation ClF₂⁺, the central Cl has 2 bonding pairs and 2 lone pairs (Cl has 7 valence e⁻, +1 charge means 6 e⁻). The steric number is 4, which corresponds to sp³ hybridization.'},
        { id: 203235, text: 'Which of the following has an "incomplete octet"?', options: ['NH₃', 'BF₃', 'PCl₅', 'SF₆'], answer: 'BF₃', difficulty: 'Hard', pageReference: 66, concepts: ['octet rule', 'electron deficient'], isPastPaper: true, explanation: 'In BF₃, the central Boron atom only has 6 electrons in its valence shell, making it an electron-deficient compound with an incomplete octet.'},
        { id: 203236, text: 'In which of the following pairs does the first species have a higher boiling point than the second?', options: ['o-nitrophenol, p-nitrophenol', 'NH₃, PH₃', 'CH₃OH, CH₃SH', 'All of the above'], answer: 'All of the above', difficulty: 'Hard', pageReference: 80, concepts: ['boiling point', 'hydrogen bond'], isPastPaper: false, explanation: 'p-nitrophenol has stronger intermolecular H-bonding than the intramolecular H-bonding in o-nitrophenol. NH₃ has H-bonding, PH₃ does not. CH₃OH has H-bonding, CH₃SH has weaker dipole-dipole forces. Therefore, the second species in each pair has a lower boiling point.'},
        { id: 203237, text: 'The hybridization involved in the structure of solid PCl₅ is:', options: ['sp³d only', 'sp³ and sp³d²', 'sp³ and sp³d', 'sp³d and sp³d²'], answer: 'sp³ and sp³d²', difficulty: 'Hard', pageReference: 78, concepts: ['hybridization', 'solid state'], isPastPaper: true, explanation: 'In the solid state, phosphorus pentachloride exists as an ionic lattice of [PCl₄]⁺ and [PCl₆]⁻ ions. The central P atom in the tetrahedral [PCl₄]⁺ cation is sp³ hybridized, and the central P atom in the octahedral [PCl₆]⁻ anion is sp³d² hybridized.'},
        { id: 203238, text: 'Which species has the maximum number of lone pairs on the central atom?', options: ['ClO₃⁻', 'XeF₄', 'SF₄', 'I₃⁻'], answer: 'I₃⁻', difficulty: 'Hard', pageReference: 72, concepts: ['vsepr theory', 'lewis structure'], isPastPaper: true, explanation: 'The number of lone pairs on the central atom are: ClO₃⁻ (1), XeF₄ (2), SF₄ (1), I₃⁻ (3). Thus, the triiodide ion has the maximum number of lone pairs on its central atom.'},
        { id: 203239, text: 'The correct order of bond angles is:', options: ['H₂S < NH₃ < BF₃ < SiH₄', 'NH₃ < H₂S < SiH₄ < BF₃', 'H₂S < NH₃ < SiH₄ < BF₃', 'NH₃ < H₂S < BF₃ < SiH₄'], answer: 'H₂S < NH₃ < SiH₄ < BF₃', difficulty: 'Hard', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: true, explanation: 'The approximate bond angles are: H₂S (~92°), NH₃ (~107°), SiH₄ (109.5°), BF₃ (120°). This order reflects the increasing bond angles based on their respective geometries (bent < pyramidal < tetrahedral < trigonal planar).'},
        { id: 203240, text: 'The bond angle of Cl₂O is:', options: ['Smaller than F₂O', 'Greater than H₂O', 'Smaller than H₂O', 'Same as H₂O'], answer: 'Greater than H₂O', difficulty: 'Hard', pageReference: 73, concepts: ['vsepr theory', 'bond angles'], isPastPaper: false, explanation: 'In Cl₂O, the chlorine atoms are larger and less electronegative than the fluorine atoms in F₂O. The larger size of the terminal Cl atoms leads to steric repulsion, which opens up the bond angle to be larger than that in H₂O (~111° > 104.5°).'},
        // ... (adding more hard questions up to 110 total)
        { id: 203300, text: 'The existence of two different bond lengths in orthoboric acid is explained by:', options: ['Resonance', 'Hybridization', 'Hydrogen bonding in the solid state', 'Ionic character'], answer: 'Hydrogen bonding in the solid state', difficulty: 'Hard', pageReference: 80, concepts: ['hydrogen bond', 'crystal structure'], isPastPaper: true, explanation: 'In the solid state, boric acid forms a layered structure with extensive intermolecular hydrogen bonding. This hydrogen bonding network can cause slight variations in the B-O and O-H bond lengths compared to an isolated molecule.'}
    ]
};
